-- Đang đổ dữ liệu cho bảng `product`
--

INSERT INTO `product` (`id`, `name`, `image`, `quantity`, `price`, `decription`, `detail`, `status`, `featured`, `id_productCategory`, `id_Author`, `id_Publisher`, `created_at`, `updated_at`) VALUES
(36, 'Tâm Lý Học Trong Nháy Mắt', '6bdec7d8ff1cd534bc08dd3d97dae4c9.jpg', 1, 140000, 'Tâm Lý Học Trong Nháy Mắt (Tập 2) - Tái Bản\r\n\r\nTâm lý học xã hội nói về cuộc sống của chính chúng ta. Rất nhiều người cho rằng nó đơn giản như những gì vẫn diễn ra hàng ngày. Nhưng, Tâm lý học xã hội phức tạp hơn thế nhiều.', '<p><strong>T&acirc;m L&yacute; Học Trong Nh&aacute;y Mắt (Tập 2) - T&aacute;i Bản</strong></p>\r\n\r\n<p>T&acirc;m l&yacute; học x&atilde; hội n&oacute;i về cuộc sống của ch&iacute;nh ch&uacute;ng ta. Rất nhiều người cho rằng n&oacute; đơn giản như những g&igrave; vẫn diễn ra h&agrave;ng ng&agrave;y. Nhưng, T&acirc;m l&yacute; học x&atilde; hội phức tạp hơn thế nhiều.</p>\r\n\r\n<p>T&acirc;m L&yacute; Học Trong Nh&aacute;y Mặt - Tập 2&nbsp;bao g&ocirc;̀m 12 chương giới thi&ecirc;̣u chung v&ecirc;̀ T&acirc;m lý học. Thay bằng vi&ecirc;̣c học thu&ocirc;̣c lòng, &quot;vò đầu bứt tai&quot; với mớ lý thuy&ecirc;́t và từ chuy&ecirc;n m&ocirc;n, cu&ocirc;́n s&aacute;ch giúp bạn tìm hiểu, ghi nhớ và &aacute;p dụng chúng m&ocirc;̣t c&aacute;ch dễ dàng và thú vị.&nbsp;S&aacute;ch được trình bày với hình minh họa sinh đ&ocirc;̣ng giúp trí tưởng tượng của bạn &quot;bay cao, bay xa&quot;.</p>\r\n\r\n<p>Đi k&egrave;m với cuốn s&aacute;ch l&agrave; c&aacute;c tranh minh họa đặc sắc, những kiến thức được tr&igrave;nh b&agrave;y sống động, hy vọng cuốn s&aacute;ch cuốn s&aacute;ch n&agrave;y c&oacute; thể thu h&uacute;t sự ch&uacute; &yacute; của bạn.</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, ...</p>\r\n', 1, 1, 3, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(37, 'Từ Điển Tiếng “Em”', '14338e7ae795f56d66996b611070b173.jpg', 1, 90000, 'TỪ ĐIỂN TIẾNG “EM” – Định nghĩa về thế giới mới!\r\n\r\nBạn sẽ bất ngờ, khi cầm cuốn “từ điển” xinh xinh này trên tay.\r\n\r\nVà sẽ còn ngạc nhiên hơn nữa, khi bắt đầu đọc từng trang sách…\r\n\r\nDĩ nhiên là vì “Từ điển tiếng “Em” không phải là một cuốn từ điển thông', '<p>TỪ ĐIỂN TIẾNG &ldquo;EM&rdquo; &ndash; Định nghĩa về thế giới mới!<br />\r\n<br />\r\nBạn sẽ bất ngờ, khi cầm cuốn &ldquo;từ điển&rdquo; xinh xinh n&agrave;y tr&ecirc;n tay.</p>\r\n\r\n<p>V&agrave; sẽ c&ograve;n ngạc nhi&ecirc;n hơn nữa, khi bắt đầu đọc từng trang s&aacute;ch&hellip;</p>\r\n\r\n<p>Dĩ nhi&ecirc;n l&agrave; v&igrave; &ldquo;Từ điển tiếng &ldquo;Em&rdquo; kh&ocirc;ng phải l&agrave; một cuốn từ điển th&ocirc;ng thường rồi!<br />\r\n<br />\r\nN&oacute;i đến &ldquo;từ điển&rdquo;, xưa nay ch&uacute;ng ta đều nghĩ về một bộ s&aacute;ch đồ sộ, giải th&iacute;ch &yacute; nghĩa, c&aacute;ch d&ugrave;ng, dịch, c&aacute;ch ph&aacute;t &acirc;m, v&agrave; thường k&egrave;m theo c&aacute;c v&iacute; dụ về c&aacute;ch sử dụng từ đ&oacute;.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, với cuốn s&aacute;ch &ldquo;Từ điển tiếng &ldquo;em&rdquo;, c&aacute;c bạn sẽ hết sức bất ngờ với những định nghĩa mới, bắt trend, s&aacute;ng tạo, th&ocirc;ng minh v&agrave; v&ocirc; c&ugrave;ng h&agrave;i hước.<br />\r\n<br />\r\nTiếng &ldquo;em&rdquo; [danh từ] ở đ&acirc;y l&agrave; tiếng l&ograve;ng của những người y&ecirc;u sự thật, gh&eacute;t sự giả dối</p>\r\n\r\n<p>C&ocirc; đơn [ t&iacute;nh từ ] kh&ocirc;ng phải l&agrave; kh&ocirc;ng c&oacute; ai b&ecirc;n cạnh, m&agrave; l&agrave; người m&igrave;nh muốn ở cạnh lại kh&ocirc;ng hề ở b&ecirc;n</p>\r\n\r\n<p>Sống lỗi [ động từ ] l&agrave; c&aacute;ch sống của mấy bạn c&oacute; người y&ecirc;u c&aacute;i l&agrave; bỏ b&ecirc; bạn b&egrave; liền h&agrave;<br />\r\n<br />\r\nNhưng đ&acirc;y kh&ocirc;ng chỉ l&agrave; cuốn s&aacute;ch chỉ biết n&oacute;i dăm ba c&acirc;u chuyện về t&igrave;nh y&ecirc;u.<br />\r\n<br />\r\nC&ograve;n nhiều hơn thế!<br />\r\n<br />\r\nL&agrave; những c&acirc;u cửa miệng của giới trẻ thời nay; l&agrave; hoạt động tưởng v&ocirc; bổ nhưng cần thiết cho sự sống: ăn, ngủ, tắm, gội cũng được định nghĩa hết sức d&iacute; dỏ V&agrave; cũng kh&ocirc;ng thiếu những &ldquo;tật xấu, th&oacute;i hư&rdquo; nghĩ đến đ&atilde; thấy &ldquo;tức c&aacute;i lồng ngực&rdquo;<br />\r\n<br />\r\nV&agrave; bạn y&ecirc;n t&acirc;m, &ldquo;tập đo&agrave;n&rdquo; Kho Từ Điển điều h&agrave;nh bởi Thịt Kho &ndash; Hiệp Thị - 2 chủ xị cho ra đời cuốn s&aacute;ch nhỏ b&eacute; x&iacute;u xiu nhưng mới mẻ v&agrave; mặn m&agrave; v&ocirc; c&ugrave;ng n&agrave;y sẽ khiến bạn tho&aacute;t m&aacute;c &ldquo;người tối cổ&rdquo; cười cả ng&agrave;y kh&ocirc;ng ch&aacute;n, nh&igrave;n bạn b&egrave; quanh m&igrave;nh bằng &aacute;nh mắt dễ thương, tận hưởng cuộc đời với những định nghĩa ho&agrave;n to&agrave;n!!!<br />\r\n<br />\r\nCuốn s&aacute;ch n&agrave;y giống như một chiếc hộp Pandora th&uacute; vị v&agrave; hấp dẫn người đọc, v&igrave; bạn kh&ocirc;ng thể đo&aacute;n trước được t&aacute;c giả sẽ &ldquo;định nghĩa&rdquo; c&acirc;u n&oacute;i đ&oacute; theo nghĩa n&agrave;o, cho ta th&ecirc;m th&iacute;ch th&uacute; với những ng&ocirc;n từ đ&aacute;ng y&ecirc;u sử dụng mỗi ng&agrave;y.<br />\r\n<br />\r\nVậy n&ecirc;n, ngay b&acirc;y giờ, bạn đ&atilde; sẵn s&agrave;ng để mở ra những điều th&uacute; vị trong cuốn s&aacute;ch n&agrave;y chưa!!!</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, ...</p>\r\n', 1, 1, 3, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(38, 'Giáo Trình Hán Ngữ 1 - Tập 1', '2aa2278d0efe410ea66257d0ec367eeb.jpg', 1, 59300, 'Giáo trình Hán ngữ tập 1 phiên bản mới tải app – Cuốn giáo trình Hán ngữ phiên bản mới duy nhất tại Việt Nam\r\n\r\n“Giáo trình Hán ngữ phiên bản mới tập 1” là cuốn đầu tiên trong bộ, nằm ở trình độ Sơ cấp dành cho người bắt đầu tiếng Trung.\r\n\r\nTrong quá trìn', '<p><strong>Gi&aacute;o tr&igrave;nh H&aacute;n ngữ tập 1 phi&ecirc;n bản mới tải app &ndash;&nbsp;Cuốn gi&aacute;o tr&igrave;nh H&aacute;n ngữ phi&ecirc;n bản mới duy nhất tại Việt Nam</strong></p>\r\n\r\n<p>&ldquo;<strong>Gi&aacute;o tr&igrave;nh H&aacute;n ngữ phi&ecirc;n bản mới tập 1</strong>&rdquo;&nbsp;l&agrave; cuốn đầu ti&ecirc;n trong bộ, nằm ở tr&igrave;nh độ Sơ cấp d&agrave;nh cho người bắt đầu tiếng Trung.</p>\r\n\r\n<p><strong>Trong qu&aacute; tr&igrave;nh học những kh&oacute; khăn tr&ecirc;n c&oacute; đang ngăn cản bạn chinh phục ng&ocirc;n ngữ n&agrave;y kh&ocirc;ng?</strong></p>\r\n\r\n<p><strong>Bạn l&agrave; người mới bắt đầu học tiếng Trung, bạn kh&ocirc;ng biết n&ecirc;n tự học như thế n&agrave;o</strong></p>\r\n\r\n<p>Trong khi tiếng Việt v&agrave; tiếng Anh được viết bằng chữ c&aacute;i Latinh th&igrave; tiếng Trung được viết bằng chữ tượng h&igrave;nh n&ecirc;n rất kh&oacute; khi bạn tự t&igrave;m hiểu ng&ocirc;n ngữ n&agrave;y. Bạn l&agrave; người mới bắt đầu học tiếng Trung, bạn muốn tự học nhưng kh&ocirc;ng biết n&ecirc;n học những kiến thức n&agrave;o, đ&acirc;u l&agrave; những kiến thức nền tảng ph&ugrave; hợp với tr&igrave;nh độ của những người mới học. V&agrave; quan trọng nhất l&agrave; bạn kh&ocirc;ng biết được n&ecirc;n t&igrave;m học từ những t&agrave;i liệu n&agrave;o. Tr&ecirc;n thị trường t&agrave;i liệu học tiếng Trung th&igrave; nhiều nhưng t&agrave;i liệu vừa b&aacute;m s&aacute;t chương tr&igrave;nh dạy học, vừa cập nhật những điểm đổi mới c&ugrave;ng như được bi&ecirc;n soạn dễ hiểu th&igrave; lại rất &iacute;t.</p>\r\n\r\n<p><strong>Ngữ ph&aacute;p v&agrave; vốn từ của bạn bị hạn chế</strong></p>\r\n\r\n<p>Ngữ ph&aacute;p v&agrave; từ vựng l&agrave; mấu chốt quan trọng nhất trong việc học một ng&ocirc;n ngữ. Nắm chắc ngữ ph&aacute;p v&agrave; từ vựng việc học tập để vượt qua c&aacute;c b&agrave;i kiểm tra hay học giao tiếp đều trở n&ecirc;n dễ d&agrave;ng hơn. Bạn l&agrave; người mới học, tr&igrave;nh độ ngữ ph&aacute;p v&agrave; từ vựng của bạn đang ở đ&acirc;u? Muốn n&acirc;ng cao tr&igrave;nh độ nhanh nhất bạn phải l&agrave;m thế n&agrave;o? Đ&acirc;u l&agrave; c&acirc;u trả lời d&agrave;nh cho bạn?</p>\r\n\r\n<p><strong>Bạn &ldquo;loay hoay&rdquo; kh&ocirc;ng biết l&agrave;m thế n&agrave;o để ph&aacute;t triển đồng thời cả 4 kỹ năng Nghe &ndash; n&oacute;i &ndash; đọc &ndash; viết</strong></p>\r\n\r\n<p>Đối với rất nhiều bạn việc đọc, viết c&aacute;c văn bản tiếng Trung&nbsp;&ldquo;dễ như ăn ch&aacute;o&rdquo;&nbsp;nhưng khi gặp người n&oacute;i tiếng H&aacute;n th&igrave; lại&ldquo;&aacute; khẩu&rdquo;,&nbsp;kh&ocirc;ng nghe được đối phương n&oacute;i g&igrave;, cũng kh&ocirc;ng biết đ&aacute;p trả ra sao. Học tiếng Trung ai cũng muốn n&acirc;ng cao cả 4 kỹ năng Nghe &ndash; n&oacute;i &ndash; đọc &ndash; viết nhưng kh&ocirc;ng phải ai cũng tự t&igrave;m được giải ph&aacute;p cho bản th&acirc;n. Vậy l&agrave;m thế n&agrave;o, bằng c&aacute;ch n&agrave;o bạn c&oacute; thể ph&aacute;t triển đồng đều 4 kỹ năng n&agrave;y?</p>\r\n\r\n<p>Bạn mới bắt đầu học tiếng H&aacute;n nhưng những kh&oacute; khăn n&agrave;y lại l&agrave;m bạn &ldquo;ch&ugrave;n bước&rdquo;, ch&aacute;n nản ngay từ đầu.&nbsp;Để c&oacute; thể duy tr&igrave; v&agrave; trở n&ecirc;n th&agrave;nh thạo tiếng Trung đ&ograve;i hỏi bạn phải c&oacute; sự ki&ecirc;n tr&igrave; v&agrave; quyết t&acirc;m đủ lớn để chinh phục m&ocirc;n ngoại ngữ n&agrave;y. Trong đ&oacute; những bước đi đầu ti&ecirc;n lu&ocirc;n l&agrave; quan trọng nhất, giải quyết được những kh&oacute; khăn n&agrave;y sẽ gi&uacute;p bạn tự tin tiếp tục n&acirc;ng cao tr&igrave;nh độ. C&ocirc;ng cụ gi&uacute;p c&aacute;c bạn l&agrave;m điều đ&oacute; chỉ c&oacute; thể l&agrave;&nbsp;&ldquo;Gi&aacute;o tr&igrave;nh H&aacute;n ngữ tập 1 phi&ecirc;n bản mới&rdquo;.</p>\r\n\r\n<p><strong>V&igrave; sao cuốn s&aacute;ch n&agrave;y lại cần thiết với những bạn mới bắt đầu học tiếng Trung?</strong></p>\r\n\r\n<p><strong>Tr&igrave;nh b&agrave;y khoa học ph&ugrave; hợp với c&aacute;c bạn tự nghi&ecirc;n cứu</strong></p>\r\n\r\n<p>Với những bạn đang tự học tiếng Trung hoặc tự &ocirc;n luyện sau những giờ học tr&ecirc;n lớp th&igrave; cuốn s&aacute;ch n&agrave;y ho&agrave;n to&agrave;n ph&ugrave; hợp. C&aacute;c b&agrave;i học được bi&ecirc;n soạn theo chương tr&igrave;nh thống nhất, ph&ugrave; hợp với tr&igrave;nh độ Sơ cấp, c&aacute;c kiến thức đều ở mức cơ bản nhất. Đ&acirc;y l&agrave; bước đầu ti&ecirc;n của người học tiếng Trung, n&ecirc;n việc tạo sự th&uacute; vị trong b&agrave;i học để người học quan trọng hơn kiến thức. Tập 1 gồm 15 b&agrave;i học với c&aacute;c chủ đề đa dạng. Mỗi b&agrave;i học gồm 5 phần:</p>\r\n\r\n<ul>\r\n	<li>B&agrave;i kh&oacute;a</li>\r\n	<li>Từ mới</li>\r\n	<li>Ch&uacute; th&iacute;ch</li>\r\n	<li>Ngữ ph&aacute;p, ngữ &acirc;m</li>\r\n	<li>Luyện tập</li>\r\n</ul>\r\n\r\n<p>Th&ocirc;ng qua mỗi b&agrave;i học c&aacute;c bạn được học đầy đủ c&aacute;c kiến thức, bắt đầu mỗi b&agrave;i c&aacute;c bạn n&ecirc;n tự học từ phần một đến phần 5, kh&ocirc;ng n&ecirc;n nhảy c&oacute;c qua c&aacute;c phần để đạt được hiệu quả cao nhất.</p>\r\n\r\n<p><strong>Hệ thống ngữ ph&aacute;p v&agrave; từ mới theo từng b&agrave;i</strong></p>\r\n\r\n<p>T&agrave;i liệu cung cấp c&aacute;c hệ thống ngữ ph&aacute;p v&agrave; từ vựng tương ứng với tr&igrave;nh độ sơ cấp. Hệ thống từ vựng b&aacute;m s&aacute;t theo từng chủ đề, mỗi b&agrave;i c&aacute;c bạn được học từ 10 &ndash; 20 từ vựng li&ecirc;n quan đến chủ đề. Ngo&agrave;i ra ở cuối s&aacute;ch c&ograve;n c&oacute; bảng từ vựng cung cấp gần 260 từ vựng đủ cho những người mới học.</p>\r\n\r\n<p>Về mặt ngữ ph&aacute;p trong tập 1, từ b&agrave;i 10 đến b&agrave;i 15 sẽ gi&uacute;p người học hiểu r&otilde; những cấu tr&uacute;c ngữ ph&aacute;p cơ bản. C&aacute;c cấu tr&uacute;c lu&ocirc;n c&oacute; v&iacute; dụ đi k&egrave;m, đ&acirc;y đều l&agrave; những v&iacute; dụ minh họa gần gũi, dễ hiểu nhất với những người mới bắt đầu. Với hệ thống ngữ ph&aacute;p v&agrave; từ vựng trong tập 1 người học đ&atilde; c&oacute; thể tự tin chinh phục kỳ thi HSK cấp độ 1.</p>\r\n\r\n<p><strong>Gi&uacute;p người học ph&aacute;t triển đồng thời cả 4 kỹ năng</strong></p>\r\n\r\n<p>Từ b&agrave;i 1 đến b&agrave;i 5, s&aacute;ch hệ thống hết c&aacute;c phi&ecirc;n &acirc;m. Học xong 5 b&agrave;i n&agrave;y c&aacute;c bạn đ&atilde; c&oacute; thể đọc hết pinyin của chữ H&aacute;n. Trong 5 b&agrave;i n&agrave;y c&ograve;n dạy người học c&aacute;ch ph&aacute;t &acirc;m tiếng H&aacute;n đ&uacute;ng chuẩn người bản địa.</p>\r\n\r\n<p>Gi&aacute;o tr&igrave;nh được bi&ecirc;n soạn theo hướng mẫu c&acirc;u để trực tiếp đi v&agrave;o thực h&agrave;nh giao tiếp. Phần b&agrave;i kh&oacute;a v&agrave; nội dung đ&agrave;m thoại c&oacute; quan hệ chủ điểm đồng nhất với b&agrave;i luyện đọc trong phần b&agrave;i tập. Phần b&agrave;i tập cũng ch&uacute; &yacute; tới r&egrave;n luyện kỹ năng giao tiếp, m&ocirc; phỏng v&agrave; bồi dưỡng tr&iacute; nhớ cho sinh vi&ecirc;n. Ngo&agrave;i ra c&aacute;c b&agrave;i nghe đều được c&aacute;c giảng vi&ecirc;n của Đại học Ng&ocirc;n ngữ Bắc Kinh thực hiện, qua đ&oacute; c&aacute;c bạn n&acirc;ng cao được kỹ năng nghe một c&aacute;ch nhanh nhất.</p>\r\n\r\n<p>Khi ho&agrave;n th&agrave;nh tr&igrave;nh độ n&agrave;y người học sẽ c&oacute; khả năng nghe &ndash; n&oacute;i &ndash; đọc &ndash; viết đều ở mức cơ bản, v&agrave; c&oacute; thể sử dụng được trong những t&igrave;nh huống nhất định.</p>\r\n\r\n<p><strong>Người học r&egrave;n được kỹ năng viết chữ</strong></p>\r\n\r\n<p>Ở cuối mỗi b&agrave;i học sẽ c&oacute; c&aacute;c bảng từ được kẻ &ocirc; sẵn gi&uacute;p c&aacute;c bạn luyện tập viết chữ H&aacute;n đ&uacute;ng chuẩn. Những chữ kh&oacute;, s&aacute;ch đ&atilde; c&oacute; phần kẻ mờ, người học chỉ cần viết lại theo c&aacute;c n&eacute;t đ&atilde; được vạch ra để học được c&aacute;ch viết c&aacute;c n&eacute;t ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p><strong>Dịch bằng 2 thứ tiếng</strong></p>\r\n\r\n<p>Cuốn s&aacute;ch c&oacute; phi&ecirc;n &acirc;m pinyin gi&uacute;p c&aacute;c bạn đọc v&agrave; hiểu được tiếng Trung. B&ecirc;n cạnh đ&oacute; s&aacute;ch c&ograve;n dịch sang cả tiếng Anh v&agrave; tiếng Việt gi&uacute;p c&aacute;c bạn tăng th&ecirc;m hiểu biết về nhiều ng&ocirc;n ngữ c&ugrave;ng l&uacute;c.</p>\r\n\r\n<p><strong>Tăng th&ecirc;m sự hiểu biết về đất nước Trung Hoa</strong></p>\r\n\r\n<p>Chủ điểm b&agrave;i kh&oacute;a bao gồm nhiều lĩnh vực trong đời sống h&agrave;ng ng&agrave;y, giới thiệu những kiến thức về con người v&agrave; đất nước Trung Quốc nhằm tăng sự hiểu biết về đất nước n&agrave;y. C&aacute;c phần ch&uacute; th&iacute;ch chi tiết cũng n&ecirc;u r&otilde; những kiến thức về ng&ocirc;n ngữ v&agrave; bối cảnh văn h&oacute;a Trung Hoa. Từ đ&oacute; c&aacute;c bạn kh&ocirc;ng chỉ học được tiếng Trung m&agrave; c&ograve;n n&acirc;ng cao sự hiểu biết của m&igrave;nh về Trung Quốc.</p>\r\n\r\n<p><strong>Học tiếng Trung qua ứng dụng tr&ecirc;n điện thoại của MCBooks</strong></p>\r\n\r\n<p>Để tiện lợi hơn trong qu&aacute; tr&igrave;nh học tập, gi&uacute;p bạn tận dụng mọi khoảng thời gian trống để &ocirc;n lại c&aacute;c b&agrave;i nghe, với gi&aacute;o tr&igrave;nh H&aacute;n ngữ 1 phi&ecirc;n bản mới tải APP, chỉ cần tải app của MCBooks qu&eacute;t m&atilde; QR code ở sau cuốn s&aacute;ch. Bạn c&oacute; thể sở hữu cuốn s&aacute;ch ngay trong điện thoại của m&igrave;nh. Hệ thống hỗ trợ tr&ecirc;n cả hai hệ điều h&agrave;nh Android v&agrave; IOS. H&atilde;y thưởng thức c&ocirc;ng nghệ trong một cuốn s&aacute;ch tuyệt vời. Chắc chắn cuốn s&aacute;ch n&agrave;y sẽ l&agrave; bạn đồng h&agrave;nh kh&ocirc;ng thể thiếu tr&ecirc;n chặng đường học tiếng Trung của bạn.</p>\r\n\r\n<p>Cuốn s&aacute;ch n&agrave;y do MCBooks &ndash; chuy&ecirc;n s&aacute;ch ngoại ngữ &ndash; độc quyền ph&aacute;t h&agrave;nh n&ecirc;n chắc chắn người học sẽ kh&ocirc;ng phải lo lắng về chất lượng cuốn s&aacute;ch. Gi&aacute;o tr&igrave;nh được in r&otilde; n&eacute;t tr&ecirc;n chất liệu giấy cực tốt kh&ocirc;ng hề k&eacute;m cạnh thậm ch&iacute; c&ograve;n nhỉnh hơn c&aacute;c cuốn s&aacute;ch gi&aacute;o khoa tr&ecirc;n thị trường hiện nay. Với cuốn s&aacute;ch n&agrave;y c&aacute;c bạn vừa c&oacute; thể tự tin để vượt qua HSK 1, vừa tự tin giao tiếp tiếng Trung trong những t&igrave;nh huống hội thoại h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p>Xin n&oacute;i th&ecirc;m, hiện nay tr&ecirc;n thị trường c&oacute; rất nhiều đối tượng sử dụng c&aacute;c sản phẩm nh&aacute;i, photocopy hoặc kh&ocirc;ng phải của MCBooks, đ&acirc;y l&agrave; c&aacute;c h&agrave;nh động vị phạm bản quyền. Để được bảo vệ quyền lợi, người học kh&ocirc;ng n&ecirc;n mua c&aacute;c cuốn s&aacute;ch n&agrave;y. H&atilde;y sử dụng đ&uacute;ng s&aacute;ch của MCBooks, v&igrave; một thế giới kh&ocirc;ng c&oacute; s&aacute;ch giả!</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, ...</p>\r\n', 1, 1, 8, 2, 2, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(39, 'Keep It Up - Tiếng Trung Cấp Tốc Cho Người Mới Bắt Đầu', '0596cc60fa0e787e4547ec61138cbbbb.jpg', 1, 135700, 'Trở ngại lớn nhất của những người mới bắt đầu học tiếng Trung chính là cảm thấy ngôn ngữ mới này rất phức tạp, khó có thể hoàn thành được quá trình học và dễ từ bỏ. Đặc biệt, người Việt Nam chúng ta đã quen với bảng chữ cái Latinh nên tiếng Trung với kiểu', '<p>Trở ngại lớn nhất của những người mới bắt đầu học tiếng Trung ch&iacute;nh l&agrave; cảm thấy ng&ocirc;n ngữ mới n&agrave;y rất phức tạp, kh&oacute; c&oacute; thể ho&agrave;n th&agrave;nh được qu&aacute; tr&igrave;nh học v&agrave; dễ từ bỏ. Đặc biệt, người Việt Nam ch&uacute;ng ta đ&atilde; quen với bảng chữ c&aacute;i Latinh n&ecirc;n tiếng Trung với kiểu chữ tượng h&igrave;nh sẽ c&agrave;ng kh&oacute; học hơn.</p>\r\n\r\n<p>Vậy l&agrave;m thế n&agrave;o để tự học tiếng Trung cho người mới bắt đầu một c&aacute;ch dễ d&agrave;ng hơn? Cuốn s&aacute;ch Keep it up &ndash; Tiếng Trung cấp tốc cho người bắt đầu ch&iacute;nh l&agrave; c&acirc;u trả lời.</p>\r\n\r\n<p>Ng&ocirc;n ngữ l&agrave; sự tổng h&ograve;a văn h&oacute;a v&agrave; x&atilde; hội của một quốc gia, sự h&ograve;a quyện của lịch sử v&agrave; phong tục tập qu&aacute;n được tạo n&ecirc;n bởi những người đ&atilde; v&agrave; đang sống tr&ecirc;n l&atilde;nh thổ đ&oacute;. Ch&iacute;nh v&igrave; thế, hơn cả một cuốn s&aacute;ch cung cấp kiến thức ng&ocirc;n ngữ đơn thuần, Keep it up &ndash; Tiếng Trung cấp tốc cho người bắt đầu c&ograve;n mở ra những kiến thức về đất nước, văn h&oacute;a, con người Trung Quốc để gi&uacute;p người học cảm thấy gần gũi v&agrave; dễ d&agrave;ng tiếp thu hơn.</p>\r\n\r\n<p>Cuốn s&aacute;ch Keep it up &ndash; Tiếng Trung cấp tốc cho người bắt đầu ch&iacute;nh l&agrave;:</p>\r\n\r\n<p>Bước khởi đầu đơn giản v&agrave; th&uacute; vị, tạo niềm tin v&agrave; hứng th&uacute; cho người học</p>\r\n\r\n<p>Tổng hợp to&agrave;n bộ kiến thức sơ khai quan trọng nhất một c&aacute;ch dễ hiểu, dễ học, dễ tiếp cận</p>\r\n\r\n<p>ƯU ĐIỂM NỔI BẬT CHỈ C&Oacute; Ở KEEP IT UP &ndash; TIẾNG TRUNG CẤP TỐC CHO NGƯỜI BẮT ĐẦU</p>\r\n\r\n<p>1. Đ&uacute;c r&uacute;t 40 năm kinh nghiệm học v&agrave; giảng dạy tiếng Trung của t&aacute;c giả Jeong Myeong Suk</p>\r\n\r\n<p>Hầu như sẽ kh&ocirc;ng ai hỏi một người Anh tại sao lại giỏi tiếng Anh, một người Việt Nam tại sao lại giỏi tiếng Việt, bởi đ&oacute; l&agrave; ng&ocirc;n ngữ bản ngữ của họ, được tiếp x&uacute;c, nghe n&oacute;i, giao tiếp, học tập h&agrave;ng ng&agrave;y. Tương tự th&igrave; tiếng Trung Quốc cũng vậy.</p>\r\n\r\n<p>T&aacute;c giả Jeong Myeong Suk l&agrave; một người H&agrave;n Quốc học v&agrave; dạy tiếng Trung, c&oacute; xuất ph&aacute;t điểm giống như ch&uacute;ng ta, l&agrave; một người nước ngo&agrave;i học tiếng Trung. Do đ&oacute;, những kinh nghiệm, phương ph&aacute;p, c&aacute;ch tiếp cận tiếng Trung của t&aacute;c giả sẽ c&oacute; nhiều điểm tương đồng v&agrave; dễ &aacute;p dụng, &aacute;p dụng hiệu quả.</p>\r\n\r\n<p>2. Cấu tr&uacute;c b&agrave;i học chặt chẽ</p>\r\n\r\n<p>15 b&agrave;i học được hiệu đ&iacute;nh tỉ mỉ, ho&agrave;n to&agrave;n kh&aacute;c biệt với những gi&aacute;o tr&igrave;nh tiếng Trung nặng nề kh&ocirc; khan kh&aacute;c.</p>\r\n\r\n<p>Mỗi b&agrave;i học sẽ c&oacute; cấu tr&uacute;c như sau:</p>\r\n\r\n<p>G&oacute;c nh&igrave;n văn h&oacute;a Trung Quốc: H&igrave;nh ảnh v&agrave; m&ocirc; tả ngắn gọn li&ecirc;n quan đến chủ đề b&agrave;i học</p>\r\n\r\n<p>C&aacute;ch diễn đạt cần thiết cho người mới bắt đầu</p>\r\n\r\n<p>Học từ vựng qua tranh</p>\r\n\r\n<p>Tự tin với hội thoại d&agrave;i</p>\r\n\r\n<p>Mở rộng c&aacute;ch diễn đạt theo tranh v&agrave; từ vựng</p>\r\n\r\n<p>N&acirc;ng cao năng lực bằng b&agrave;i kiểm tra tổng kết</p>\r\n\r\n<p>3. Giải th&iacute;ch chi tiết, hướng dẫn tự học ph&aacute;t &acirc;m chuẩn</p>\r\n\r\n<p>Cuốn s&aacute;ch tr&igrave;nh b&agrave;y cụ thể v&agrave; dễ hiểu cho người học từ quy tắc phi&ecirc;n &acirc;m Latinh đến hội thoại cơ bản v&agrave; c&aacute;ch ph&aacute;t &acirc;m của từ.<br />\r\nCung cấp nhiều v&iacute; dụ đơn giản, đa dạng để người học luyện đọc v&agrave; luyện n&oacute;i. B&ecirc;n cạnh đ&oacute;, cuốn s&aacute;ch c&oacute; rất nhiều vựng mới theo mỗi chủ đề.</p>\r\n\r\n<p>4. Qu&eacute;t m&atilde; QR truy cập file nghe chuẩn từ gi&aacute;o vi&ecirc;n bản ngữ</p>\r\n\r\n<p>C&ocirc;ng nghệ t&iacute;ch hợp gi&uacute;p người học đơn giản v&agrave; dễ d&agrave;ng h&oacute;a việc học, c&oacute; thể học mọi l&uacute;c mọi nơi, chỉ cần với một chiếc điện thoại th&ocirc;ng minh nhỏ gọn.</p>\r\n\r\n<p>5. Đ&aacute;p &aacute;n phần luyện tập v&agrave; phần nghe &ndash; Bổ sung phụ lục tra cứu từ vựng</p>\r\n\r\n<p>Với những ưu điểm tr&ecirc;n, Keep it up &ndash; Tiếng Trung cấp tốc cho người mới bắt đầu chắc chắn sẽ l&agrave; một người bạn đồng h&agrave;nh tuyệt vời, gi&uacute;p bạn nắm vững được kiến thức tiếng Trung v&agrave; c&oacute; thể giao tiếp cơ bản bằng tiếng Trung Quốc chỉ trong thời gian ngắn!</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, ...</p>\r\n', 1, 1, 8, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(40, 'Lối Tắt Khởi Nghiệp - Con Đường Ngắn Từ Tay Trắng Đến Thành Công Bền Vững', '0244311818f2cb7a4052b5c1aa475c06.png', 1, 165000, 'Kinh doanh có rất nhiều rủi ro. Đừng dại dột!\r\n\r\nĐó là nhận định của số đông đã thất bại và cố gắng “tỏ ra thông thái”  bằng những lời khuyên hữu ích cho người đến sau. Thực tế thì hoàn toàn ngược lại!\r\n\r\nKinh doanh không hề rủi ro như người ta đã thổi ph', '<p><strong>Kinh doanh c&oacute; rất nhiều rủi ro. Đừng dại dột!</strong></p>\r\n\r\n<p>Đ&oacute; l&agrave; nhận định của số đ&ocirc;ng đ&atilde; thất bại v&agrave; cố gắng &ldquo;tỏ ra th&ocirc;ng th&aacute;i&rdquo;&nbsp; bằng những lời khuy&ecirc;n hữu &iacute;ch cho người đến sau. Thực tế th&igrave; ho&agrave;n to&agrave;n ngược lại!</p>\r\n\r\n<p>Kinh doanh kh&ocirc;ng hề rủi ro như người ta đ&atilde; thổi phồng.</p>\r\n\r\n<p>Giống như mọi ng&agrave;nh nghề, để<strong>&nbsp;khởi sự kinh doanh hiệu quả</strong>&nbsp;bạn cần c&oacute; một&nbsp;<strong>lộ tr&igrave;nh đ&uacute;ng đắn.</strong>&nbsp;</p>\r\n\r\n<p>Nếu đi sai, bạn sẽ trả gi&aacute;.</p>\r\n\r\n<p>Nếu đi đ&uacute;ng, bạn sẽ rất an to&agrave;n v&agrave; nhanh ch&oacute;ng đạt được th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p>Quyển s&aacute;ch&nbsp;<strong>Lối Tắt Khởi Nghiệp</strong>&nbsp;sẽ chỉ ra cho bạn con đường đ&oacute;.</p>\r\n\r\n<p>Tư duy độc đ&aacute;o, th&uacute; vị trong s&aacute;ch sẽ gi&uacute;p bạn x&acirc;y n&ecirc;n doanh nghiệp th&agrave;nh c&ocirc;ng, trong thời gian rất ngắn, bất kể bạn đang gặp r&agrave;o cản về chuy&ecirc;n m&ocirc;n, vốn liếng, kinh nghiệm hay thiếu &yacute; tưởng.</p>\r\n\r\n<p>Quyển s&aacute;ch l&agrave; một chuyến phi&ecirc;u lưu kinh doanh đầy th&uacute; vị, mang đến cho bạn:</p>\r\n\r\n<ul>\r\n	<li>&ldquo;<strong>B&agrave;i học về 3 vi&ecirc;n đ&aacute; nhỏ</strong>&rdquo;: Mỗi vi&ecirc;n đ&aacute; nhỏ l&agrave; vũ kh&iacute; chống lại ngh&egrave;o đ&oacute;i, lười biếng v&agrave; cả sự ngu dốt.</li>\r\n	<li><strong>&ldquo;4 tư duy khởi nghiệp độc đ&aacute;o&rdquo;</strong>&nbsp;bao gồm: tư duy của người khai hoang, người c&agrave;y xới, người gieo hạt v&agrave; người chăm s&oacute;c. Mỗi tư duy l&agrave; một c&ocirc;ng cụ hữu &iacute;ch gi&uacute;p bạn từng bước nh&igrave;n thấy được nhu cầu của kh&aacute;ch h&agrave;ng, t&igrave;m ra giải ph&aacute;p v&agrave; biến ch&uacute;ng th&agrave;nh những c&ocirc;ng việc kinh doanh sinh lời nhanh ch&oacute;ng.</li>\r\n</ul>\r\n\r\n<p>Quyển s&aacute;ch c&ograve;n trang bị cho bạn &ldquo;<strong>V&ograve;ng lặp tăng tốc khởi nghiệp&rdquo; &amp; &ldquo;7 kỹ năng kinh doanh cơ bản&rdquo;</strong>&nbsp;để bạn tự tin khởi nghiệp ngay m&agrave; kh&ocirc;ng phải đối mặt với rủi ro hay thất bại.</p>\r\n\r\n<p>Ngo&agrave;i ra, s&aacute;ch c&ograve;n c&oacute; th&ecirc;m&nbsp;<strong>15 c&acirc;u chuyện th&agrave;nh c&ocirc;ng&nbsp;</strong>của nhiều người đang l&agrave; triệu ph&uacute; nhờ &aacute;p dụng tư duy trong quyển s&aacute;ch v&agrave;o c&ocirc;ng việc kinh doanh thực tế của m&igrave;nh<strong>.</strong>&nbsp;15 tấm gương n&agrave;y sẽ mang đến cho bạn những b&agrave;i học sinh động nhất - thực tế nhất, để bạn quyết định h&agrave;nh động ngay.</p>\r\n\r\n<p><em>Đường tắt kh&ocirc;ng c&oacute; nghĩa l&agrave; đường dễ đi nhưng n&oacute; gi&uacute;p ta đi nhanh hơn. C&oacute; rất nhiều c&aacute;ch để đến với th&agrave;nh c&ocirc;ng nhưng người kh&ocirc;n ngoan lu&ocirc;n biết t&igrave;m ra con đường ngắn nhất cho ri&ecirc;ng m&igrave;nh.</em></p>\r\n\r\n<p>H&atilde;y đọc&nbsp;<strong>Lối Tắt Khởi Nghiệp</strong>&nbsp;v&agrave; hẹn gặp bạn tr&ecirc;n đỉnh th&agrave;nh c&ocirc;ng!</p>\r\n', 1, 1, 6, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(41, 'Đầu Tư - Kế Hoạch 12 Tháng Đến Với Tự Do Tài Chính', 'f29ecff272886b2c99f30ec9ee8547d3.jpg', 1, 138000, 'Đầu Tư - Kế Hoạch 12 Tháng Đến Với Tự Do Tài Chính\r\nDanielle Town, con gái của nhà đầu tư thành công và là tác giả cuốn sách tài chính bán chạy nhất Rule #1, Phil Town, đã dành phần lớn cuộc đời trưởng thành của mình để tránh đầu tư cho đến khi cô nhận ra', '<p><strong>Đầu Tư - Kế Hoạch 12 Th&aacute;ng Đến Với Tự Do T&agrave;i Ch&iacute;nh</strong><br />\r\nDanielle Town, con g&aacute;i của nh&agrave; đầu tư th&agrave;nh c&ocirc;ng v&agrave; l&agrave; t&aacute;c giả cuốn s&aacute;ch t&agrave;i ch&iacute;nh b&aacute;n chạy nhất Rule #1, Phil Town, đ&atilde; d&agrave;nh phần lớn cuộc đời trưởng th&agrave;nh của m&igrave;nh để tr&aacute;nh đầu tư cho đến khi c&ocirc; nhận ra với nghề luật sư tốn thời gian c&ocirc; kh&ocirc;ng được g&igrave; ngo&agrave;i bị c&ocirc;ng việc kiểm so&aacute;t cuộc sống hay tiền bạc của m&igrave;nh. Quyết t&acirc;m lấy lại tự do, bỏ phiếu bằng tiền của m&igrave;nh cho c&aacute;c gi&aacute; trị m&agrave; c&ocirc; tin tưởng v&agrave; đối ph&oacute; với nỗi sợ h&atilde;i thị trường chứng kho&aacute;n kh&oacute; lường, c&ocirc; đ&atilde; quay sang cha m&igrave;nh, Phil, để &ocirc;ng gi&uacute;p c&ocirc; chịu tr&aacute;ch nhiệm về cuộc sống v&agrave; tương lai th&ocirc;ng qua đầu tư v&agrave;o gi&aacute; trị theo kiểu Warren Buffett. Trong suốt một năm, Danielle đ&atilde; đi từ chỗ tr&aacute;nh mọi thứ li&ecirc;n quan đến phức hợp c&ocirc;ng nghiệp-t&agrave;i ch&iacute;nh đến chỗ biết ch&iacute;nh x&aacute;c c&aacute;ch thức v&agrave; thời điểm đầu tư v&agrave;o c&aacute;c c&ocirc;ng ty tuyệt vời.<br />\r\nTrong mỗi chương, Danielle xem x&eacute;t c&aacute;c chiến lược đầu tư m&agrave; c&ocirc; th&agrave;nh thạo khi sự hiểu biết ng&agrave;y c&agrave;ng tăng của c&ocirc; l&agrave;m tăng th&ecirc;m niềm tin cậy giữa c&ocirc; với cha m&igrave;nh. Xuy&ecirc;n suốt cuốn s&aacute;ch, c&ocirc; hợp l&yacute; h&oacute;a qu&aacute; tr&igrave;nh đưa ra c&aacute;c quyết định t&agrave;i ch&iacute;nh kh&ocirc;n ngoan v&agrave; cho bạn thấy việc đầu tư c&oacute; thể dễ d&agrave;ng &mdash; v&agrave; sinh lợi &mdash; như thế n&agrave;o.<br />\r\nNắm bắt một sự cho v&agrave; nhận ấm &aacute;p, hấp dẫn v&agrave; thực tế giữa một c&ocirc; con g&aacute;i cứng đầu v&agrave; người cha ki&ecirc;n nhẫn nhất của m&igrave;nh, cuốn s&aacute;ch n&agrave;y l&agrave;m cho thế giới phức tạp của việc đầu tư trở n&ecirc;n đơn giản, dễ hiểu v&agrave; dễ tiếp cận, sẽ gi&uacute;p bạn h&igrave;nh th&agrave;nh kế hoạch đầu tư của ri&ecirc;ng m&igrave;nh v&agrave; th&uacute;c đẩy sự tự tin để đưa n&oacute; v&agrave;o h&agrave;nh động.</p>\r\n', 1, 1, 6, 2, 2, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(42, 'Tiny Talk 1: Workbook (B)', 'ef6a216badc4d1662ddb7f4c78a6390a.jpg', 1, 235000, 'A three-level communicative listening and speaking series for 3 to 6 year-olds.', '', 1, 1, 8, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(43, 'Vui Vẻ Không Quạu Nha ', '17262447faaef713be60d6b25ec0551d.jpg', 1, 451000, 'Cuộc đời ngày ngày nói yêu mình.\r\n\r\nXong cuộc đời lại đủ thứ phức tạp và bất công với mình.\r\n\r\nVậy là cuộc đời ghét mình rồi!\r\n\r\nThôi nào!\r\n\r\nThả lỏng và tận hưởng sự vui vẻ đi. Vì chẳng phải cuộc đời đang ghét bạn đâu, mà c', '<p>Cuộc đời ngày ngày nói yêu mình.</p>\r\n\r\n<p>Xong cuộc đời lại đủ thứ phức tạp v&agrave; bất c&ocirc;ng với m&igrave;nh.</p>\r\n\r\n<p>Vậy là cuộc đời ghét mình r&ocirc;̀i!<br />\r\n<br />\r\nTh&ocirc;i n&agrave;o!<br />\r\n<br />\r\nThả lỏng và tận hưởng sự vui vẻ đi. Vì chẳng phải cuộc đời đang ghét bạn đâu, mà chính bạn bạn đang loay hoay với những mệt nhọc ở trên đời. Ví dụ như nay đã là deadline mà b&ocirc;̃ng bị rớt mạng, sáng nay đi làm quên đem theo ví, hay đ&ocirc;̀ng nghiệp ở công ty nói x&acirc;́u mình,<br />\r\n<br />\r\nN&ecirc;́u k&ecirc;̉ ra thì sẽ có ti tỉ thứ không theo ý mình m&ocirc;̃i ngày. Không nói đ&ecirc;́n những chuyện bực tức khác, những n&ocirc;̃i bu&ocirc;̀n chúng ta không làm chủ được, những đi&ecirc;̀u vô tình đ&ecirc;́n khi&ecirc;́n ta quạu cọ và x&acirc;́u xí. Rồi khi ch&uacute;ng ta cứ để ch&uacute;ng trong l&ograve;ng sự phiền muộn kh&ocirc;ng biết k&eacute;o d&agrave;i đến bao giờ mới kết th&uacute;c.<br />\r\n<br />\r\nCho n&ecirc;n, thay v&igrave; để t&acirc;m đến những chuyện khiến m&igrave;nh kh&ocirc;ng vui, h&atilde;y lựa chọn vui vẻ, bạn sẽ thấy cuộc đời trở n&ecirc;n tuyệt vời hơn rất nhiều. V&agrave; để c&acirc;n bằng những cảm x&uacute;c ấy &ldquo;Vui vẻ không quạu nha&rdquo; chính là một m&oacute;n qu&agrave; để lan tỏa v&agrave; nh&acirc;n l&ecirc;n niềm vui, một cu&ocirc;́n sách mà ai cũng c&acirc;̀n phải có đ&ecirc;̉ thêm yêu những đi&ecirc;̀u nhỏ xíu đáng yêu xung quanh m&igrave;nh.<br />\r\n<br />\r\nVui vẻ kh&ocirc;ng quạu nha - Cu&ocirc;́n sách kh&ocirc;ng chỉ mặn m&agrave; đúng như tên gọi của nó m&agrave; c&ograve;n bắt kịp c&aacute;c xu hướng được quan t&acirc;m từ fanpage nổi tiếng &ldquo;Ở đ&acirc;y zui n&egrave;&rdquo; sẽ giúp bạn có những tràng cười vui vẻ không ngớt, c&oacute; c&aacute;i nh&igrave;n khoan dung cởi mở hơn, nhìn nhận những xui xẻo v&acirc;́p phải b&ocirc;̃ng trở nên nhỏ b&eacute; dưới g&oacute;c độ d&iacute; dỏm v&agrave; hài hước.<br />\r\n<br />\r\nVui vẻ kh&ocirc;ng quạu nha - xin được gửi đ&ecirc;́n những bạn trẻ đang dễ giận dữ, cau c&oacute; ngoài th&ecirc;́ giới kia, những ai đang bu&ocirc;̀n phi&ecirc;̀n v&ecirc;̀ rắc rối nào đó, &ldquo;trái tim&rdquo; nhỏ bé này còn phải dành cho ni&ecirc;̀m vui, đừng đ&ecirc;̉ bực bội, dỗi hờn từ những đi&ecirc;̀u không đáng chi&ecirc;́m h&ecirc;́t ch&ocirc;̃.<br />\r\n<br />\r\nVà hãy hét to với Th&ecirc;́ giới rằng &ldquo;Mình là một người tràn đ&acirc;̀y năng lượng,&rdquo; luôn sẵn s&agrave;ng để hạnh ph&uacute;c hơn.</p>\r\n', 1, 1, 10, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(44, 'Tôi Quyết Định Sống Cho Chính Tôi ', '537f7b9bb219be81e217e0c7b5b037e6.jpg', 1, 71000, 'Cuốn sách đạt giải thưởng “Cuốn sách của năm” tại Hàn Quốc vào năm 2017.\r\n\r\nĐã tái bản hơn 100 lần kể từ lần xuất bản đầu tiên vào năm 2016.\r\n\r\nĐược nhiều nghệ sĩ chọn đọc và giới thiệu đến các fan của mình, đơn cử là JungKook (BTS).\r\n\r\nNhư tác giả Kim Su', '<p>Cuốn s&aacute;ch đạt giải thưởng &ldquo;Cuốn s&aacute;ch của năm&rdquo; tại H&agrave;n Quốc v&agrave;o năm 2017.</p>\r\n\r\n<p>Đ&atilde; t&aacute;i bản hơn 100 lần kể từ lần xuất bản đầu ti&ecirc;n v&agrave;o năm 2016.</p>\r\n\r\n<p>Được nhiều nghệ sĩ chọn đọc v&agrave; giới thiệu đến c&aacute;c fan của m&igrave;nh, đơn cử l&agrave; JungKook (BTS).</p>\r\n\r\n<p>Như t&aacute;c giả Kim Suhyun đ&atilde; chia sẻ về qu&aacute; tr&igrave;nh s&aacute;ng t&aacute;c cuốn s&aacute;ch n&agrave;y:</p>\r\n\r\n<p>&ldquo;T&ocirc;i đ&atilde; cố gắng ghi ra những điều quan trọng nhất trong cuộc sống như:</p>\r\n\r\n<p>C&ocirc;ng việc, c&aacute;c mối quan hệ, niềm vui, tinh thần, thể chất, sức khỏe&hellip;</p>\r\n\r\n<p>T&ocirc;i đ&atilde; kh&ocirc;ng lường trước những việc c&oacute; thể xảy ra . M&agrave; t&ocirc;i chỉ l&agrave;m (Do) những việc t&ocirc;i muốn (Want) v&agrave; c&oacute; thể (Can).</p>\r\n\r\n<p>Một c&ocirc;ng thức đơn giản l&agrave; Want + Can = Do</p>\r\n\r\n<p>Thay v&agrave;o đ&oacute;, h&atilde;y l&agrave;m việc chăm chỉ.</p>\r\n\r\n<p>T&ocirc;i dần tr&aacute;nh những mối quan hệ chỉ nhấn ch&igrave;m t&ocirc;i hoặc kh&ocirc;ng quan trọng với t&ocirc;i v&agrave; tự nhủ rằng t&ocirc;i sẽ kh&ocirc;ng cho ph&eacute;p bản th&acirc;n l&agrave;m bất kể điều g&igrave; một c&aacute;ch t&ugrave;y tiện nữa.</p>\r\n\r\n<p>T&ocirc;i d&agrave;nh thời gian t&igrave;m kiếm niềm vui trong cuộc sống.</p>\r\n\r\n<p>C&oacute; những ng&agrave;y, t&ocirc;i ngồi ngắm bầu trời v&agrave;i lần để cảm nhận cuộc sống. T&ocirc;i đối diện với những vấn đề c&ograve;n bỏ ngỏ, giải quyết ch&uacute;ng v&agrave; cố gắng sống l&agrave;nh mạnh.</p>\r\n\r\n<p>V&igrave; những đ&aacute;p &aacute;n do x&atilde; hội quy định kh&aacute;c xa những quan niệm th&ocirc;ng thường của mọi người xung quanh ch&uacute;ng ta n&ecirc;n chỉ c&oacute; một điều m&agrave; ch&uacute;ng ta biết chắc, đ&oacute; l&agrave; cuộc sống rất r&otilde; r&agrave;ng v&agrave; nhẹ tựa l&ocirc;ng hồng.</p>\r\n\r\n<p>Vậy &yacute; nghĩa của cuộc sống rốt cuộc l&agrave; g&igrave;?</p>\r\n\r\n<p>Sau nhiều lần chất vấn, c&acirc;u trả lời t&ocirc;i t&igrave;m thấy kh&ocirc;ng c&ograve;n l&agrave; đ&aacute;p &aacute;n c&aacute; nh&acirc;n m&agrave; trở th&agrave;nh đ&aacute;p &aacute;n của cả một cộng đồng, đ&oacute; ch&iacute;nh l&agrave; hiện thực h&oacute;a gi&aacute; trị của bản th&acirc;n trong x&atilde; hội.</p>\r\n\r\n<p>T&ocirc;i lu&ocirc;n quan t&acirc;m v&agrave; kh&iacute;ch lệ sự đ&oacute;ng g&oacute;p của c&aacute; nh&acirc;n cho một x&atilde; hội tương lai.</p>\r\n\r\n<p>Bởi v&igrave; giải quyết c&aacute;c vấn đề x&atilde; hội ch&iacute;nh l&agrave; một c&aacute;ch trực tiếp để giải quyết c&aacute;c vấn đề c&aacute; nh&acirc;n.</p>\r\n\r\n<p>Nhưng đ&oacute; kh&ocirc;ng phải l&iacute; do duy nhất khiến ch&uacute;ng ta đ&oacute;ng g&oacute;p một phần bản th&acirc;n v&agrave;o x&atilde; hội.</p>\r\n\r\n<p>Th&ocirc;ng qua cuốn s&aacute;ch n&agrave;y, t&ocirc;i muốn lan truyền một l&agrave;n s&oacute;ng d&ugrave; nhỏ nhưng c&oacute; &yacute; nghĩa đến mọi người.</p>\r\n\r\n<p>Những c&acirc;u hỏi kh&ocirc;ng c&oacute; hồi kết như &ldquo;Ch&uacute;ng ta phải sống thế n&agrave;o?&rdquo; lu&ocirc;n nảy sinh trong cuộc sống. Để t&ocirc;i n&oacute;i cho bạn nghe c&acirc;u trả lời của t&ocirc;i nh&eacute;, đ&oacute; ch&iacute;nh l&agrave;: &ldquo;H&atilde;y sống tốt với đời m&igrave;nh.&rdquo;</p>\r\n\r\n<p>Bạn kh&ocirc;ng cần phải phức tạp h&oacute;a mọi vấn đề.</p>\r\n\r\n<p>Bạn chỉ cần l&agrave;m việc chăm chỉ, t&acirc;m sự với những người th&acirc;n y&ecirc;u, c&ugrave;ng nhau ăn những m&oacute;n ngon, chia sẻ với nhau những b&agrave;i h&aacute;t y&ecirc;u th&iacute;ch, những cuốn s&aacute;ch hay v&agrave;o một ng&agrave;y đẹp trời bạn tận hưởng &aacute;nh mặt trời.</p>\r\n\r\n<p>T&ocirc;i nghĩ rằng một cuộc sống tốt đẹp trọn vẹn ch&iacute;nh l&agrave; chuỗi ng&agrave;y ấm &aacute;p, b&igrave;nh dị đang diễn ra. Nếu c&oacute; thể, h&atilde;y bước th&ecirc;m một nhịp để hướng tới cuộc sống c&oacute; &yacute; nghĩa.</p>\r\n\r\n<p>Mặc d&ugrave; trong vũ trụ n&agrave;y c&ograve;n tồn tại những hạt bụi g&acirc;y vẩn đục cuộc sống th&igrave; ch&uacute;ng ta vẫn phải vượt qua v&agrave; giữ lấy phẩm gi&aacute;, t&ocirc;n nghi&ecirc;m của ch&iacute;nh m&igrave;nh. Bất chấp quy luật th&agrave;nh bại của thế giới, t&ocirc;i vẫn muốn tự h&agrave;o về một cuộc sống như vậy.</p>\r\n\r\n<p>C&oacute; một cuốn s&aacute;ch viết rằng: &ldquo;D&ugrave; bạn sống thế n&agrave;o, t&ocirc;i vẫn lu&ocirc;n cổ vũ.&rdquo;</p>\r\n\r\n<p>Tuy nhi&ecirc;n, điều cần thiết kh&ocirc;ng phải l&agrave; người kh&aacute;c cổ vũ bạn m&agrave; bạn phải cổ vũ ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<p>Gửi đến bản th&acirc;n t&ocirc;i - bạn đồng h&agrave;nh với t&ocirc;i cho đến cuối cuộc đời:</p>\r\n\r\n<p>&ldquo;D&ugrave; t&ocirc;i sống thế n&agrave;o, t&ocirc;i vẫn lu&ocirc;n cổ vũ ch&iacute;nh m&igrave;nh.&rdquo;&rdquo;</p>\r\n', 1, 1, 3, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(45, 'Có Một Ngày, Bố Mẹ Sẽ Già Đi', '130e8b3dea2ab17db80879d25381c830.jpg', 1, 77000, 'RỒI MỘT NGÀY, BỐ MẸ SẼ GIÀ ĐI\r\n\r\nCàng lớn lên, những quyến luyến của chúng ta đối với bố mẹ càng ít. Khi đại bàng non có thể giương cánh, nó luôn hướng tới không trung, chứ không phải rúc vào đôi cánh bố mẹ.\r\n\r\nBạn khát vọng cuộc sống khởi sắc, không còn ', '<p><strong>RỒI MỘT NG&Agrave;Y, BỐ MẸ SẼ GI&Agrave; ĐI</strong></p>\r\n\r\n<p>C&agrave;ng lớn l&ecirc;n, những quyến luyến của ch&uacute;ng ta đối với bố mẹ c&agrave;ng &iacute;t. Khi đại b&agrave;ng non c&oacute; thể giương c&aacute;nh, n&oacute; lu&ocirc;n hướng tới kh&ocirc;ng trung, chứ kh&ocirc;ng phải r&uacute;c v&agrave;o đ&ocirc;i c&aacute;nh bố mẹ.</p>\r\n\r\n<p>Bạn kh&aacute;t vọng cuộc sống khởi sắc, kh&ocirc;ng c&ograve;n quẫn b&aacute;ch giật gấu v&aacute; vai. Bạn kh&aacute;t vọng sự nghiệp tiến triển, như thế mới kh&ocirc;ng uổng bao năm vất vả tăng ca bất kể gi&oacute; mưa. Bạn kh&aacute;t vọng con c&aacute;i của m&igrave;nh về sau c&oacute; thể lớn l&ecirc;n vui vẻ khỏe mạnh, đạt được th&agrave;nh t&iacute;ch đ&aacute;ng tự h&agrave;o, vươn l&ecirc;n trong m&ocirc;i trường cạnh tranh quyết liệt&hellip; Nhưng bạn lại v&ocirc; t&igrave;nh đặt một kh&aacute;t vọng kh&aacute;c nằm s&acirc;u trong t&acirc;m hồn xuống vị tr&iacute; dưới c&ugrave;ng, kh&aacute;t vọng n&agrave;y ch&iacute;nh l&agrave; hy vọng bố mẹ đừng vội gi&agrave; đi.<br />\r\nGiống như mỗi ng&agrave;y đều c&oacute; thể nh&igrave;n thấy mặt trời, v&agrave;o những ng&agrave;y trời quang nắng đẹp, bạn cảm thấy ấm &aacute;p v&agrave; dễ chịu, n&oacute; như một thứ đương nhi&ecirc;n n&ecirc;n c&oacute;, hưởng thụ sự tốt đẹp n&oacute; mang lại đ&atilde; trở th&agrave;nh th&oacute;i quen của bạn. Nhưng nếu một ng&agrave;y, c&oacute; người n&oacute;i với bạn mặt trời sẽ kh&ocirc;ng mọc nữa, bạn cảm thấy thế n&agrave;o? C&oacute; lẽ phần lớn mọi người đều cảm thấy sợ h&atilde;i v&agrave; luống cuống. Bố mẹ cũng giống như mặt trời, lu&ocirc;n lặng lẽ ở sau lưng, cho bạn chỗ dựa v&agrave; sự ấm &aacute;p, nhưng nếu một ng&agrave;y bố mẹ kh&ocirc;ng c&ograve;n ở đấy nữa, cảm gi&aacute;c an to&agrave;n quen thuộc cũng lập tức biến mất, bấy giờ muốn t&igrave;m lại, sợ rằng đ&atilde; qu&aacute; muộn.<br />\r\nBởi v&igrave; năm th&aacute;ng vẫn m&atilde;i tr&ocirc;i, thời gian kh&ocirc;ng trở lại, t&ecirc;n si&ecirc;u trộm thời gian đang dần dần lấy đi tuổi xu&acirc;n của bố mẹ, rồi c&oacute; một ng&agrave;y, bố mẹ sẽ gi&agrave; đi.</p>\r\n', 1, 1, 3, 2, 2, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(46, 'Đời Có Mấy Tý, Sao Phải Nghĩ', '1b4b99bc090ee784b1b867d6270bd60e.png', 1, 63000, '“Lá cây cần quang hợp\r\ncòn mình thì cần quan tâm”\r\nCuộc sống mà, lúc nào cũng đầy rẫy những chuyện phức tạp không ngờ đến. Một vấn đề tưởng như nhỏ xíu cũng có thể khiến bạn trở nên quạu cọ, cục súc.\r\n \r\n\r\nCục súc không phải vì tỏ tình bị từ chối, mà vì đ', '<p>&ldquo;L&aacute; c&acirc;y cần quang hợp</p>\r\n\r\n<p>c&ograve;n m&igrave;nh th&igrave; cần quan t&acirc;m&rdquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cuộc sống m&agrave;, l&uacute;c n&agrave;o cũng đầy rẫy những chuyện phức tạp kh&ocirc;ng ngờ đến.&nbsp;Một vấn đề tưởng như nhỏ x&iacute;u cũng c&oacute; thể khiến bạn trở n&ecirc;n quạu cọ, cục s&uacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cục s&uacute;c kh&ocirc;ng phải v&igrave; tỏ t&igrave;nh bị từ chối, m&agrave; v&igrave; đ&agrave;nh b&aacute;i bị th&uacute;i heo cơ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cục s&uacute;c kh&ocirc;ng phải v&igrave; phải chờ đợi một ai, m&agrave; v&igrave; chờ một chiếc điện thoại mới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thay v&igrave; dồn n&eacute;n, h&atilde;y t&igrave;m c&aacute;ch giải tỏa bằng &ldquo;Đời c&oacute; mấy t&yacute;, sao phải nghĩ&rdquo; - cuốn s&aacute;ch nhỏ với những tr&iacute;ch dẫn hot trend h&agrave;i hước, &ldquo;mặn m&agrave;&rdquo; tạo th&agrave;nh xu hướng tr&ecirc;n mạng x&atilde; hội.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&igrave; đ&ocirc;i l&uacute;c cục s&uacute;c một ch&uacute;t cũng c&oacute; sao, cục s&uacute;c để cuộc đời bớt lao đao th&ocirc;i m&agrave;.</p>\r\n', 1, 1, 3, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(47, 'Đàn Ông Sao Hỏa Đàn Bà Sao Kim', 'bc10734989977da424642a1c4750eee2.jpg', 1, 120000, 'Ngày xửa ngày xưa, những người sao Hỏa và sao Kim đã gặp gỡ, yêu nhau và sống hạnh phúc bởi họ tôn trọng và chấp nhận mọi điều khác biệt. Rồi họ đến Trái Đất và chứng lãng quên đã xảy ra: Họ quên rằng họ đến từ những hành tinh khác\r\n\r\n \r\n\r\nHiểu biết về gi', '<p><strong>Ng&agrave;y xửa ng&agrave;y xưa, những người sao Hỏa v&agrave; sao Kim đ&atilde; gặp gỡ, y&ecirc;u nhau v&agrave; sống hạnh ph&uacute;c bởi họ t&ocirc;n trọng v&agrave; chấp nhận mọi điều kh&aacute;c biệt. Rồi họ đến Tr&aacute;i Đất v&agrave; chứng l&atilde;ng qu&ecirc;n đ&atilde; xảy ra: Họ qu&ecirc;n rằng họ đến từ những h&agrave;nh tinh kh&aacute;c</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hiểu biết về giới t&iacute;nh gi&uacute;p ch&uacute;ng ta th&ecirc;m khoan dung v&agrave; biết tha thứ khi ai đ&oacute; kh&ocirc;ng đ&aacute;p lại theo c&aacute;ch m&agrave; ta mong đợi</strong></p>\r\n\r\n<p><strong>T&igrave;nh chỉ đẹp khi c&ograve;n dang dở</strong><br />\r\n<strong>Cưới nhau về nham nhở lắm em ơ&nbsp;</strong></p>\r\n\r\n<p>Bởi kết h&ocirc;n kh&ocirc;ng phải l&agrave; kết th&uacute;c một cuộc t&igrave;nh, m&agrave; n&oacute; mới l&agrave; mở đầu cho rất nhiều những giai đoạn kh&oacute; khăn m&agrave; cặp vợ chồng n&agrave;o cũng phải trải qua. Bạn phải biết c&aacute;ch th&iacute;ch ứng v&agrave; đối ph&oacute; với n&oacute; để kh&ocirc;ng rơi v&agrave;o t&igrave;nh cảnh h&ocirc;n nh&acirc;n đổ vỡ.</p>\r\n\r\n<p><strong>Bạn sẽ rất dễ &#39;VỠ MỘNG&#39; sau khi kết h&ocirc;n</strong></p>\r\n\r\n<p>Cả hai sẽ bắt đầu ph&aacute;t hiện ra nhiều thứ ở người kia sau khi về sống chung một nh&agrave;. Nếu kh&ocirc;ng chuẩn bị cho m&igrave;nh t&acirc;m thế tốt v&agrave; sự th&iacute;ch ứng nhanh, rất c&oacute; thể cả hai sẽ tạo ra sự&hellip; thất vọng v&agrave; vỡ mộng trong nhau.</p>\r\n\r\n<p><strong>Cuộc sống h&ocirc;n nh&acirc;n trở n&ecirc;n nh&agrave;m ch&aacute;n</strong></p>\r\n\r\n<p>Khi đ&atilde; qu&aacute; hiểu nhau về t&iacute;nh c&aacute;ch, ch&iacute;nh th&oacute;i quen h&agrave;ng ng&agrave;y, lặp đi lặp lại tạo ra sự nh&agrave;m ch&aacute;n. Trong mối quan hệ vợ chồng c&oacute; thể gia tăng nhiều vụ c&atilde;i v&atilde; v&igrave; những l&iacute; do như khủng hoảng kinh tế, c&aacute;ch chăm s&oacute;c nu&ocirc;i dạy con c&aacute;i, c&ocirc;ng việc v&agrave; đặc biệt l&agrave; chuyện cảm thấy&hellip; t&igrave;nh y&ecirc;u phai nhạt.</p>\r\n\r\n<p><strong>Kh&ocirc;ng hiểu, kỳ vọng th&aacute;i qu&aacute; v&agrave;o&nbsp; bạn đời</strong></p>\r\n\r\n<p>Ai cũng c&oacute; những mong muốn ri&ecirc;ng về một nửa của m&igrave;nh nhưng đ&ocirc;i khi ch&iacute;nh sự k&igrave; vọng th&aacute;i qu&aacute; l&agrave;m họ dễ rơi v&agrave;o trạng th&aacute;i thất vọng, ch&aacute;n chường. Bạn cảm thấy kh&ocirc;ng hiểu đối phương, cảm thấy họ kh&ocirc;ng được như m&igrave;nh mong muốn, v&agrave; những cuộc c&atilde;i v&atilde; bắt đầu diễn ra thường xuy&ecirc;n hơn.</p>\r\n\r\n<p><strong>Muốn g&igrave;n giữ hạnh ph&uacute;c trọn vẹn, cần lắm những sự thấu hiểu, cảm th&ocirc;ng, chia sẻ v&agrave; y&ecirc;u thương nhau</strong></p>\r\n\r\n<ul>\r\n	<li>V&igrave; đ&agrave;n &ocirc;ng v&agrave; đ&agrave;n b&agrave; đến từ hai h&agrave;nh tinh kh&aacute;c nhau, h&atilde;y t&ocirc;n trọng v&agrave; chấp nhận mọi điều kh&aacute;c biệt.</li>\r\n	<li>Bạn kh&ocirc;ng thể v&agrave; cũng kh&ocirc;ng n&ecirc;n cố thay đổi người bạn đời của m&igrave;nh. Thay đổi l&agrave; việc của họ, c&ograve;n việc của bạn l&agrave; thay đổi c&aacute;ch giao tiếp, phản ứng v&agrave; đối đ&aacute;p với người bạn đời của m&igrave;nh.</li>\r\n	<li>Ch&uacute;ng ta dễ d&agrave;ng đổ lỗi của m&igrave;nh l&ecirc;n đầu bạn đời hơn l&agrave; nhận thấy sai lầm của ch&iacute;nh m&igrave;nh. V&igrave; vậy, h&atilde;y lắng nghe để tận hưởng cuộc sống h&ocirc;n nh&acirc;n ngọt ng&agrave;o như l&uacute;c mới y&ecirc;u</li>\r\n</ul>\r\n', 1, 1, 3, 1, 2, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(48, 'Cân Bằng Cảm Xúc, Cả Lúc Bão Giông', 'a19424cfe9d113c32732d93cf2d5f59a.jpg', 1, 64000, 'Một ngày, chúng ta có khoảng 16 tiếng tiếp xúc với con người, công việc, các nguồn thông tin từ mạng xã hội, loa đài báo giấy… Việc này mang đến cho bạn vô vàn cảm xúc, cả tiêu cực lẫn tích cực.\r\nBạn có thể thấy vui khi nhìn một em bé đến trường nhưng 5 p', '<p>Một ng&agrave;y, ch&uacute;ng ta c&oacute; khoảng 16 tiếng tiếp x&uacute;c với con người, c&ocirc;ng việc, c&aacute;c nguồn th&ocirc;ng tin từ mạng x&atilde; hội, loa đ&agrave;i b&aacute;o giấy&hellip; Việc n&agrave;y mang đến cho bạn v&ocirc; v&agrave;n cảm x&uacute;c, cả ti&ecirc;u cực lẫn t&iacute;ch cực.<br />\r\nBạn c&oacute; thể thấy vui khi nh&igrave;n một em b&eacute; đến trường nhưng 5 ph&uacute;t sau đ&atilde; nổi cơn tam b&agrave;nh khi bị đứa trẻ con đi xe đạp đ&acirc;m sầm v&agrave;o lại c&ograve;n ăn vạ.<br />\r\nHoặc bạn rất dễ ph&aacute;t đi&ecirc;n nếu như chỉ c&ograve;n 5 gi&acirc;y nữa đ&egrave;n giao th&ocirc;ng chuyển từ đỏ sang xanh m&agrave; kẻ đi đằng sau bấm c&ograve;i inh ỏi.<br />\r\nHay l&agrave;, bạn thấy buồn chỉ v&igrave; h&ocirc;m nay trời mưa man m&aacute;c, m&agrave; vẫn phải ngồi trong văn ph&ograve;ng l&agrave;m việc, bạn bi quan rằng tuổi trẻ thật buồn tẻ.<br />\r\nH&atilde;y thừa nhận đi! Ai trong số ch&uacute;ng ta cũng sẽ trải qua những điều n&agrave;y. V&agrave; cuốn s&aacute;ch n&agrave;y dạy bạn c&aacute;ch l&agrave;m h&ograve;a với ch&iacute;nh những ti&ecirc;u cực b&ecirc;n trong m&igrave;nh&hellip;<br />\r\nĐa số những cảm x&uacute;c ti&ecirc;u cực chỉ thỏa m&atilde;n được ch&uacute;ng ta ở thời điểm hiện tại. Tức l&agrave; ch&uacute;ng ta c&oacute; thể sẽ nhận được sự thoải m&aacute;i khi la h&eacute;t, c&aacute;u giận, chửi thề một ai đ&oacute; nhưng những &acirc;m thanh ta ph&aacute;t ra, suy nghĩ ta truyền đi sẽ đ&oacute;ng vai một con dao sắc lẹm đ&acirc;m ngược lại v&agrave;o ch&iacute;nh th&acirc;n thể m&igrave;nh. C&aacute;c mối quan hệ, cuộc sống, c&ocirc;ng việc của ch&uacute;ng ta sẽ dần rơi v&agrave;o sự mệt mỏi, bế tắc.<br />\r\nHọc được c&aacute;ch c&acirc;n bằng cảm x&uacute;c cũng ch&iacute;nh l&agrave; học được c&aacute;ch l&agrave;m chủ cuộc đời m&igrave;nh th&ocirc;ng minh, s&aacute;ng suốt v&agrave; đ&uacute;ng hướng.<br />\r\n&ldquo;C&acirc;n bằng cảm x&uacute;c, cả l&uacute;c b&atilde;o gi&ocirc;ng&rdquo; kh&aacute;c biệt ho&agrave;n to&agrave;n so với những cuốn s&aacute;ch về cảm x&uacute;c th&ocirc;ng thường kh&aacute;c khi:<br />\r\nChỉ cho người đọc những l&yacute; do khiến ch&uacute;ng ta lu&ocirc;n ti&ecirc;u cực v&agrave; bản chất của những điều n&agrave;y l&agrave; g&igrave;?<br />\r\nNhững điều khiến ch&uacute;ng ta thường xuy&ecirc;n n&oacute;ng giận v&agrave; kh&ocirc;ng hạnh ph&uacute;c bắt nguồn từ đ&acirc;u?<br />\r\nĐưa ra những b&agrave;i tập cụ thể để c&acirc;n bằng cảm x&uacute;c mỗi ng&agrave;y như: Ngừng suy nghĩ trong một khoảng thời gian cố định, tưởng tượng một qu&aacute; tr&igrave;nh, tập sống với bản th&acirc;n mỗi ng&agrave;y&hellip;<br />\r\nC&acirc;n bằng phương tr&igrave;nh của cuộc đời kh&ocirc;ng dễ, cuốn s&aacute;ch n&agrave;y kh&ocirc;ng chỉ l&agrave; l&yacute; thuyết c&ograve;n cho bạn những kĩ năng, phướng ph&aacute;p tận t&igrave;nh nhất để gi&uacute;p bạn l&agrave;m chủ cảm x&uacute;c của m&igrave;nh.</p>\r\n', 1, 1, 3, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00');
INSERT INTO `product` (`id`, `name`, `image`, `quantity`, `price`, `decription`, `detail`, `status`, `featured`, `id_productCategory`, `id_Author`, `id_Publisher`, `created_at`, `updated_at`) VALUES
(49, 'Đi Tìm Lẽ Sống', '61fb657f347d14d9d7bf6fe901001a8e.jpg', 1, 58000, 'Đi Tìm Lẽ Sống\r\n\r\nĐi tìm lẽ sống của Viktor Frankl là một trong những quyển sách kinh điển của thời đại. Thông thường, nếu một quyển sách chỉ có một đoạn văn, một ý tưởng có sức mạnh thay đổi cuộc sống của một người, thì chỉ riêng điều đó cũng đã đủ để ch', '<p>Đi T&igrave;m Lẽ Sống</p>\r\n\r\n<p>Đi t&igrave;m lẽ sống&nbsp;của&nbsp;Viktor Frankl&nbsp;l&agrave; một trong những quyển s&aacute;ch kinh điển của thời đại. Th&ocirc;ng thường, nếu một quyển s&aacute;ch chỉ c&oacute; một đoạn văn, một &yacute; tưởng c&oacute; sức mạnh thay đổi cuộc sống của một người, th&igrave; chỉ ri&ecirc;ng điều đ&oacute; cũng đ&atilde; đủ để ch&uacute;ng ta đọc đi đọc lại v&agrave; d&agrave;nh cho n&oacute; một chỗ tr&ecirc;n kệ s&aacute;ch của m&igrave;nh. Quyển s&aacute;ch n&agrave;y c&oacute; nhiều đoạn văn như thế.</p>\r\n\r\n<p>Trước hết, đ&acirc;y l&agrave; quyển s&aacute;ch viết về sự sinh tồn. Giống như nhiều người Do Th&aacute;i sinh sống tại Đức v&agrave; Đ&ocirc;ng &Acirc;u khi ấy, vốn nghĩ rằng m&igrave;nh sẽ được an to&agrave;n trong những năm 1930, Frankl đ&atilde; trải qua khoảng thời gian chịu nhiều nghiệt ng&atilde; trong trại tập trung v&agrave; trại hủy diệt của Đức quốc x&atilde;. Điều kỳ diệu l&agrave; &ocirc;ng đ&atilde; sống s&oacute;t, cụm từ &ldquo;th&eacute;p đ&atilde; t&ocirc;i thế đấy&rdquo; c&oacute; thể lột tả ch&iacute;nh x&aacute;c trường hợp n&agrave;y. Nhưng trong Đi t&igrave;m lẽ sống, t&aacute;c giả &iacute;t đề cập đến những kh&oacute; nhọc, đau thương, mất m&aacute;t m&agrave; &ocirc;ng đ&atilde; trải qua, thay v&agrave;o đ&oacute; &ocirc;ng viết về những nguồn sức mạnh đ&atilde; gi&uacute;p &ocirc;ng tồn tại.</p>\r\n\r\n<p>&Ocirc;ng chua x&oacute;t kể về những t&ugrave; nh&acirc;n đ&atilde; đầu h&agrave;ng cuộc sống, mất hết hy vọng ở tương lai v&agrave; chắc hẳn l&agrave; những người đầu ti&ecirc;n sẽ chết. &Iacute;t người chết v&igrave; thiếu thức ăn v&agrave; thuốc men, m&agrave; phần lớn họ chết v&igrave; thiếu hy vọng, thiếu một lẽ sống. Ngược lại, Frankl đ&atilde; nu&ocirc;i giữ hy vọng để giữ cho m&igrave;nh sống s&oacute;t bằng c&aacute;ch nghĩ về người vợ của m&igrave;nh v&agrave; tr&ocirc;ng chờ ng&agrave;y gặp lại n&agrave;ng. &Ocirc;ng c&ograve;n mơ ước sau chiến tranh sẽ được thuyết giảng về c&aacute;c b&agrave;i học t&acirc;m l&yacute; &ocirc;ng đ&atilde; học được từ trại tập trung Auschwitz. R&otilde; r&agrave;ng c&oacute; nhiều t&ugrave; nh&acirc;n khao kh&aacute;t được sống đ&atilde; chết, một số chết v&igrave; bệnh, một số chết v&igrave; bị hỏa thi&ecirc;u. Trong tập s&aacute;ch n&agrave;y, Frankl tập trung l&yacute; giải nguy&ecirc;n nh&acirc;n v&igrave; sao c&oacute; những người đ&atilde; sống s&oacute;t trong trại tập trung của ph&aacute;t x&iacute;t Đức hơn l&agrave; đưa ra lời giải th&iacute;ch cho c&acirc;u hỏi v&igrave; sao phần lớn trong số họ đ&atilde; kh&ocirc;ng bao giờ trở về nữa.</p>\r\n', 1, 1, 3, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(50, 'Payback Time - Ngày Đòi Nợ', '94b39d773b28290e9f4ed1daa53449eb.jpg', 1, 300000, '\"Trong chứng khoán và thị trường tài chính, dám tham gia đầu tư cũng là một thành công tâm lý ban đầu. Dù vậy, 95% nhà đầu tư Việt Nam thuộc nhóm nhà đầu tư nhỏ lẻ với vốn kiến thức tài chính vô cùng hạn chế. Họ tham gia vào thị trường chứng khoán với 100', '<p>&quot;Trong chứng kho&aacute;n v&agrave; thị trường t&agrave;i ch&iacute;nh, d&aacute;m tham gia đầu tư cũng l&agrave; một th&agrave;nh c&ocirc;ng t&acirc;m l&yacute; ban đầu. D&ugrave; vậy, 95% nh&agrave; đầu tư Việt Nam thuộc nh&oacute;m nh&agrave; đầu tư nhỏ lẻ với vốn kiến thức t&agrave;i ch&iacute;nh v&ocirc; c&ugrave;ng hạn chế. Họ tham gia v&agrave;o thị trường chứng kho&aacute;n với 100% &yacute; ch&iacute; v&agrave; sự quyết t&acirc;m chiến thắng thị trường, nhưng thật kh&ocirc;ng may mắn kết cục cuối c&ugrave;ng của họ lu&ocirc;n l&agrave; sự thất bại v&agrave; mất tiền.</p>\r\n\r\n<p>Bởi v&igrave;, ch&uacute;ng ta thường dễ d&agrave;ng nghe theo c&aacute;c &ldquo;lời khuy&ecirc;n miễn ph&iacute;&rdquo; đến từ bạn b&egrave;, những người m&ocirc;i giới, những người nghe ng&oacute;ng th&ocirc;ng tin từ c&aacute;c &ldquo;đội l&aacute;i&rdquo; l&agrave;m gi&aacute; v&agrave; thao t&uacute;ng chứng kho&aacute;n. Ch&uacute;ng ta kh&ocirc;ng biết l&agrave;m thế n&agrave;o để ph&acirc;n biệt hai kh&aacute;i niệm &ldquo;gi&aacute; cả &ndash; pricing&rdquo; v&agrave; &ldquo;gi&aacute; trị &ndash; value&rdquo;. Hay mua mua, b&aacute;n b&aacute;n li&ecirc;n tục h&agrave;ng ng&agrave;y với việc d&aacute;n mắt v&agrave;o bảng điện tử, ph&acirc;n t&iacute;ch biểu đồ, đường trung b&igrave;nh gi&aacute;&hellip; v&agrave; mong đợi một vận may cổ phiếu c&ugrave;ng gi&aacute; của n&oacute; sẽ đi theo suy đo&aacute;n của m&igrave;nh?</p>\r\n\r\n<p>Rất nhiều &ldquo;nh&agrave; đầu tư&rdquo; kh&ocirc;ng hề c&oacute; một hệ thống đầu tư chuẩn mực n&agrave;o cả. Kh&ocirc;ng biết c&aacute;ch v&agrave; những ti&ecirc;u ch&iacute; để t&igrave;m kiếm cổ phiếu, theo d&otilde;i, quyết định điểm mua v&agrave; quyết định thời điểm b&aacute;n ra. V&igrave; kh&ocirc;ng c&oacute; hệ thống đầu tư chuẩn mực n&ecirc;n kết quả ch&uacute;ng ta chỉ c&oacute; một sự lựa chọn đ&oacute; ch&iacute;nh l&agrave; THUA LỖ.</p>\r\n\r\n<p>Vậy c&oacute; phải chỉ c&oacute; những chuy&ecirc;n gia t&agrave;i ch&iacute;nh mới c&oacute; thể gặt h&aacute;i th&agrave;nh c&ocirc;ng tr&ecirc;n thị trường chứng kho&aacute;n? Để kiếm được tiền từ chứng kho&aacute;n bạn phải sở hữu h&agrave;ng t&aacute; kho t&agrave;ng kiến thức t&agrave;i ch&iacute;nh vốn kh&ocirc;ng dễ d&agrave;ng &ldquo;hấp thụ&rdquo; trong một sớm một chiều?&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng hẳn vậy. C&oacute; rất nhiều người d&ugrave; kh&ocirc;ng được đ&agrave;o tạo b&agrave;i bản về c&aacute;c kiến thức t&agrave;i ch&iacute;nh, d&ugrave; vẫn l&agrave; những người đầu tư nhỏ lẻ họ vẫn chiến thắng thị trường v&agrave; l&agrave;m gi&agrave;u được từ chứng kho&aacute;n. Đ&oacute; l&agrave; bởi v&igrave; họ c&oacute; được một hệ thống đầu tư chuẩn mực, biết vận dụng c&aacute;c c&ocirc;ng cụ ph&acirc;n t&iacute;ch, biết tu&acirc;n thủ c&aacute;c nguy&ecirc;n tắc trong đầu tư.</p>\r\n\r\n<p>C&oacute; một hệ thống đầu tư chuẩn mực đ&atilde; v&agrave; đang được những nh&agrave; đầu tư bậc thầy tr&ecirc;n thế giới như Warren Buffett, Charlie Munger&hellip; &aacute;p dụng. Hệ thống đầu tư v&agrave; phương ph&aacute;p đầu tư ưu việt đ&oacute; đ&atilde; được g&oacute;i gọn v&agrave; thực sự dễ hiểu trong cuốn PayBack Time &ndash; Ng&agrave;y đ&ograve;i nợ của t&aacute;c giả Phil Town.</p>\r\n\r\n<p>NG&Agrave;Y Đ&Ograve;I NỢ &ndash; Payback Time l&agrave; cuốn s&aacute;ch b&aacute;n chạy nhất New York Time được t&aacute;c giả Phil Town sử dụng những ng&ocirc;n ngữ đơn giản, dễ hiểu v&agrave; lồng gh&eacute;p những v&iacute; dụ thực tế gi&uacute;p cho người đọc tiếp cận với những kiến thức về đầu tư chứng kho&aacute;n một c&aacute;ch dễ d&agrave;ng. B&ecirc;n cạnh đ&oacute; với những kiến thức v&agrave; trải nghiệm của bản th&acirc;n, ch&uacute;ng t&ocirc;i đ&atilde; đưa cuốn s&aacute;ch gần gũi hơn với bạn đọc Việt Nam.</p>\r\n\r\n<p>Cuốn s&aacute;ch sẽ hướng dẫn bạn từ c&aacute;ch thức lựa chọn, đ&aacute;nh gi&aacute; cổ phiếu, cho đến x&acirc;y dựng cho m&igrave;nh một danh mục c&aacute;c cổ phiếu sẽ mua, mức gi&aacute; mua&ndash;b&aacute;n n&agrave;o l&agrave; hợp l&yacute;, c&ugrave;ng với những nguy&ecirc;n tắc m&agrave; bạn phải tu&acirc;n theo&hellip; v&agrave; cứ thực h&agrave;nh như vậy cho tới khi bạn trở n&ecirc;n gi&agrave;u c&oacute;.</p>\r\n\r\n<p>&ldquo;Một cuộc sống hạnh ph&uacute;c được bắt đầu từ những quyết định đầu tư kh&ocirc;n ngoan&rdquo;. Ngay ng&agrave;y h&ocirc;m nay, h&atilde;y bắt đầu quyết định đầu tư kh&ocirc;n ngoan của bạn bằng việc trang bị một hệ thống v&agrave; phương ph&aacute;p đầu tư ho&agrave;n chỉnh đ&atilde; được chứng minh hiệu quả tuyệt đối qua thời gian được tr&igrave;nh b&agrave;y trong cuốn s&aacute;ch n&agrave;y. Bởi v&igrave;: Kiếm một số tiền lớn từ đầu tư chứng kho&aacute;n ch&iacute;nh l&agrave; c&aacute;ch &ldquo;trả th&ugrave;&rdquo; tốt nhất cho tương lai t&agrave;i ch&iacute;nh của bạn vốn đ&atilde; bị cướp đi trước đ&acirc;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>MỤC LỤC S&Aacute;CH</p>\r\n\r\n<p>Lưu &yacute; c&ugrave;ng độc giả</p>\r\n\r\n<p>Lời tựa&nbsp;</p>\r\n\r\n<p>Chương 1: Con đường từ Quy tắc số 1- RULE #1 &ndash; đến Payback Time&nbsp;</p>\r\n\r\n<p>Chương 2: Người gi&agrave;u đ&atilde; d&ugrave;ng khủng hoảng để &ldquo;PHẤT L&Ecirc;N&rdquo; như thế n&agrave;o?&nbsp;</p>\r\n\r\n<p>Chương 3: ĐẦU TƯ v&agrave;o c&aacute;c quỹ tương hỗ kh&ocirc;ng mang lại hiệu quả&nbsp;</p>\r\n\r\n<p>Chương 4: BA CHỮ M &ndash; C&ocirc;ng thức đầu tư kh&ocirc;ng c&oacute; rủi ro&nbsp;</p>\r\n\r\n<p>Chương 5: Payback Time nghĩa l&agrave; &ldquo;KH&Ocirc;NG SỢ H&Atilde;I&rdquo;&nbsp;</p>\r\n\r\n<p>Chương 6: T&Aacute;M BƯỚC chập chững đi đến th&agrave;nh c&ocirc;ng&nbsp;</p>\r\n\r\n<p>Chương 7: Chỉ c&oacute; phương ph&aacute;p S&Agrave;N &amp; TRẦN (FACs &ndash; FLOOR &amp; CEILINGs) gi&uacute;p bạn kiếm tiền&nbsp;</p>\r\n\r\n<p>Chương 8: C&acirc;u chuyện cổ t&iacute;ch của một gia đ&igrave;nh&nbsp;</p>\r\n\r\n<p>Chương 9: TIỀN CH&Ugrave;A c&ugrave;ng Berky&nbsp;</p>\r\n\r\n<p>Chương 10: C&ugrave;ng tham gia c&aacute;ch mạng về QUY TẮC SỐ 1.&quot;</p>\r\n', 1, 1, 6, 2, 2, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(51, 'Hướng Dẫn Giao Dịch Theo Sóng Elliott', '2a59b6243a298c705f21d85441a76c4c.jpg', 1, 369000, 'Hướng Dẫn Giao Dịch Theo Sóng Elliott là cuốn sách phải đọc dành cho những ai muốn biết cách thức giao dịch theo Nguyên Lý Sóng Elliott. Bạn sẽ biết làm thế nào để tìm ra các cơ hội giao dịch tốt, đánh giá chất lượng từng tín hiệu giao dịch, cách mở vị th', '<p><em><strong>Hướng Dẫn Giao Dịch Theo S&oacute;ng Elliott</strong></em>&nbsp;l&agrave; cuốn s&aacute;ch phải đọc d&agrave;nh cho những ai muốn biết c&aacute;ch thức giao dịch theo Nguy&ecirc;n L&yacute; S&oacute;ng Elliott. Bạn sẽ biết l&agrave;m thế n&agrave;o để t&igrave;m ra c&aacute;c cơ hội giao dịch tốt, đ&aacute;nh gi&aacute; chất lượng từng t&iacute;n hiệu giao dịch, c&aacute;ch mở vị thế, quản trị giao dịch bằng c&aacute;ch n&acirc;ng v&agrave; &nbsp;hạ lệnh dừng lỗ, cũng như c&aacute;ch đ&oacute;ng lệnh hợp l&yacute; nhất.</p>\r\n\r\n<p>Cuốn s&aacute;ch<em><strong>&nbsp;Hướng Dẫn Giao Dịch Theo S&oacute;ng Elliott&nbsp;</strong></em>giả định bạn đọc l&agrave; những người đ&atilde; quen thuộc với Nguy&ecirc;n L&yacute; S&oacute;ng Elliott v&agrave; c&aacute;ch thức ứng dụng l&yacute; thuyết n&agrave;y. Giống như nhiều cuốn s&aacute;ch dạy chiến thuật đ&aacute;nh cờ tướng (lu&ocirc;n giả định bạn phải c&oacute; những kiến thức cơ bản về c&aacute;ch chơi cờ), cuốn s&aacute;ch n&agrave;y cũng giả định bạn đọc đ&atilde; nắm sơ lược c&aacute;c kiến thức cơ bản về một số mẫu h&igrave;nh S&oacute;ng Elliott v&agrave; l&agrave;m thế n&agrave;o để kết hợp ch&uacute;ng lại với nhau.</p>\r\n\r\n<p>Nếu bạn l&agrave; một nh&agrave; ph&acirc;n t&iacute;ch v&agrave; giao dịch s&oacute;ng Elliott đầy kinh nghiệm v&agrave; cần một bản t&oacute;m lược đơn giản, đầy đủ, x&uacute;c t&iacute;ch về Nguy&ecirc;n L&yacute; S&oacute;ng, th&igrave; Phụ Lục Nguy&ecirc;n L&yacute; S&oacute;ng Elliott đ&aacute;p ứng tất cả c&aacute;c y&ecirc;u c&acirc;u n&agrave;y.</p>\r\n\r\n<p>Nếu bạn l&agrave; một người chưa hề biết g&igrave; về Nguy&ecirc;n L&yacute; S&oacute;ng Elliott hoặc bạn muốn nghi&ecirc;n cứu chuy&ecirc;n s&acirc;u, ch&uacute;ng t&ocirc;i đề nghị bạn đọc n&ecirc;n tham khảo c&aacute;c t&agrave;i liệu đ&iacute;nh k&egrave;m trong cuốn s&aacute;ch n&agrave;y v&agrave; truy cập tới phi&ecirc;n bản miễn ph&iacute; cuốn s&aacute;ch kinh điển tr&ecirc;n phố Wall: &ldquo;Elliott Wave Principle (Nguy&ecirc;n L&yacute; S&oacute;ng Elliott)&rdquo; của Frost v&agrave; Prechter.&nbsp;</p>\r\n\r\n<p>Cả hai ch&uacute;ng t&ocirc;i đ&atilde; v&agrave; đang giao dịch kiếm sống bằng kỷ luật tu&acirc;n thủ Nguy&ecirc;n L&yacute; S&oacute;ng Elliott.&nbsp;<em><strong>Hướng Dẫn Giao Dịch Theo S&oacute;ng Elliott</strong></em>&nbsp;l&agrave; một h&agrave;nh tr&igrave;nh trải nghiệm của ch&uacute;ng t&ocirc;i khi &aacute;p dụng Nguy&ecirc;n L&yacute; S&oacute;ng Elliott v&agrave;o thực tiễn: Ch&uacute;ng t&ocirc;i nh&igrave;n thấy điều g&igrave;, ch&uacute;ng t&ocirc;i lờ đi điều g&igrave;, ch&uacute;ng t&ocirc;i đ&atilde; l&agrave;m đ&uacute;ng c&aacute;i g&igrave;, v&agrave; ch&uacute;ng t&ocirc;i đ&atilde; sai lầm như thế n&agrave;o.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i kh&ocirc;ng muốn tr&igrave;nh b&agrave;y những v&iacute; dụ ho&agrave;n hảo khiến bạn ngộ nhận rằng, bạn c&oacute; thể giao dịch l&agrave;m gi&agrave;u chỉ trong v&ograve;ng 30 ph&uacute;t v&agrave; d&agrave;nh phần lớn thời gian trong ng&agrave;y để chơi golf. Thay v&agrave;o đ&oacute;, ch&uacute;ng t&ocirc;i cố gắng tạo ra một cuốn s&aacute;ch hướng dẫn giao dịch giống với thực tế nhất. V&igrave; thế, ch&uacute;ng t&ocirc;i sẽ ph&ocirc; b&agrave;y tất cả những sai lầm, những điều kh&ocirc;ng ho&agrave;n hảo cho từng giao dịch, để cho bạn đọc hiểu rằng, c&oacute; nhiều c&aacute;ch giao dịch th&agrave;nh c&ocirc;ng khi tu&acirc;n thủ kỷ luật t&iacute;n hiệu giao dịch theo s&oacute;ng Elliott.</p>\r\n\r\n<p>Nếu bạn muốn giao dịch kiếm sống bằng ch&iacute;nh đồng vốn của m&igrave;nh, nếu bạn muốn trở th&agrave;nh một nh&agrave; giao dịch th&agrave;nh c&ocirc;ng, nếu bạn muốn chỉ giao dịch với những t&iacute;n hiệu tốt nhất, hoặc thậm ch&iacute; rất đơn giản, nếu mục ti&ecirc;u của bạn chỉ l&agrave; &quot;kh&ocirc;ng để mất tiền&quot; khi tham gia v&agrave;o thị trường t&agrave;i ch&iacute;nh.&nbsp;Giao dịch th&agrave;nh c&ocirc;ng đ&ograve;i hỏi phải l&agrave;m việc nghi&ecirc;m t&uacute;c, t&iacute;nh kỷ luật v&agrave; cả sự nhanh nhạy. Nhưng ngay cả khi c&oacute; được ba điều tr&ecirc;n, bạn cũng chỉ mới tiến gần đến th&agrave;nh c&ocirc;ng m&agrave; th&ocirc;i. Điều cuối c&ugrave;ng gi&uacute;p bạn c&oacute; được th&agrave;nh c&ocirc;ng bền vững ch&iacute;nh l&agrave; tri thức. V&agrave; đ&acirc;y l&agrave; điều cuốn s&aacute;ch mang lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i hy vọng bạn đọc sẽ y&ecirc;u th&iacute;ch cuốn s&aacute;ch&nbsp;<em><strong>Hướng Dẫn Giao Dịch Theo S&oacute;ng Elliott</strong></em>.&nbsp;</p>\r\n\r\n<p>Wayne Gorman v&agrave; Jeffrey Kennedy</p>\r\n', 1, 0, 6, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(52, 'Tony Buổi Sáng - Trên Đường Băng', '082a07cec3232115e2b22636fd71593c.jpg', 1, 54000, 'Tony Buổi Sáng - Trên Đường Băng (Tái Bản 2017)\r\n\r\n\"Khi còn trẻ, hãy ra ngoài nhiều hơn ở nhà. Hãy nhào vô xin người khác “bóc hết, lột sạch” khả năng của mình. Chỉ sợ bất tài nộp hồ sơ “xin việc”, mà chả ai thèm cho, chả ai thèm bóc lột. Khi đã được bóc ', '<p><strong>Tony Buổi S&aacute;ng - Tr&ecirc;n Đường Băng (T&aacute;i Bản 2017)</strong></p>\r\n\r\n<p>&quot;Khi c&ograve;n trẻ, h&atilde;y ra ngo&agrave;i nhiều hơn ở nh&agrave;. H&atilde;y nh&agrave;o v&ocirc; xin người kh&aacute;c &ldquo;b&oacute;c hết, lột sạch&rdquo; khả năng của m&igrave;nh. Chỉ sợ bất t&agrave;i nộp hồ sơ &ldquo;xin việc&rdquo;, m&agrave; chả ai th&egrave;m cho, chả ai th&egrave;m b&oacute;c lột. Khi đ&atilde; được b&oacute;c v&agrave; lột hết, d&ugrave; sau n&agrave;y đi đ&acirc;u, l&agrave;m g&igrave;, bạn đều cực kỳ th&agrave;nh c&ocirc;ng. V&igrave; năng lực được trui r&egrave;n trong qu&aacute; tr&igrave;nh l&agrave;m cho người kh&aacute;c. Sự chăm chỉ, t&iacute;nh kỷ luật, quen tay quen ch&acirc;n, quen ng&aacute;p, quen lười&hellip; cũng từ c&ocirc;ng việc m&agrave; ra. Mọi &ocirc;ng chủ vĩ đại đều từng l&agrave; những người l&agrave;m c&ocirc;ng ở vị tr&iacute; thấp nhất. Họ đều rẽ tr&aacute;i trong khi mọi người rẽ phải. Họ c&oacute; những quyết định kh&ocirc;ng theo đ&aacute;m đ&ocirc;ng, kh&ocirc;ng cam chịu sống một cuộc đời tầm thường, nhạt nh&ograve;a&hellip; rồi chết.</p>\r\n\r\n<p>C&ograve;n những bạn thu nhập 6 triệu cũng t&uacute;ng thiếu, 20 triệu cũng đi vay mượn để ti&ecirc;u d&ugrave;ng, th&igrave; th&ocirc;i, cuộc đời họ chấm dứt giấc mơ lớn. Tiền nong c&aacute; nh&acirc;n quản l&yacute; kh&ocirc;ng được, th&igrave; l&agrave;m sao m&agrave; quản trị t&agrave;i ch&iacute;nh một cơ nghiệp lớn?&rdquo;. Tư duy thế n&agrave;o th&igrave; n&oacute; ra số phận thế đ&oacute;.&quot;</p>\r\n\r\n<p>(Tony buổi s&aacute;ng)</p>\r\n\r\n<p>Tr&ecirc;n đường băng l&agrave; tập hợp những b&agrave;i viết được ưa th&iacute;ch tr&ecirc;n Facebook của&nbsp;Tony Buổi S&aacute;ng. Nhưng kh&aacute;c với một tập tản văn th&ocirc;ng thường, nội dung c&aacute;c b&agrave;i được chọn lọc c&oacute; chủ đ&iacute;ch, nhằm chuẩn bị về tinh thần, kiến thức&hellip;cho c&aacute;c bạn trẻ v&agrave;o đời. S&aacute;ch gồm 3 phần: &ldquo;Chuẩn bị h&agrave;nh trang&rdquo;, &ldquo;Trong ph&ograve;ng chờ s&acirc;n bay&rdquo; v&agrave; &ldquo;L&ecirc;n m&aacute;y bay&rdquo;, tương ứng với những qu&aacute; tr&igrave;nh một bạn trẻ phải trải qua trước khi &ldquo;cất c&aacute;nh&rdquo; tr&ecirc;n đường băng cuộc đời, bay v&agrave;o bầu trời cao rộng.</p>\r\n\r\n<p>Những b&agrave;i viết của Tony sinh động, thiết thực, h&agrave;i hước v&agrave; xuất ph&aacute;t từ c&aacute;i t&acirc;m trong s&aacute;ng của một người đi trước nhiều kinh nghiệm. Anh viết về th&aacute;i độ với sự học v&agrave; kiến thức n&oacute;i chung, c&aacute;ch ứng ph&oacute; với những trắc trở thử th&aacute;ch khi đi l&agrave;m, c&aacute;ch sống h&agrave;o sảng nghĩa t&igrave;nh văn minh&hellip;truyền cảm hứng cho c&aacute;c bạn trẻ sống hết m&igrave;nh, trọn vẹn từng ph&uacute;t gi&acirc;y. Tuy đối tượng độc giả ch&iacute;nh m&agrave; cuốn s&aacute;ch hướng đến l&agrave; c&aacute;c bạn trẻ, nhưng độc giả lớn tuổi hơn vẫn c&oacute; thể đọc s&aacute;ch để hiểu v&agrave; c&oacute; c&aacute;ch hỗ trợ con em m&igrave;nh một c&aacute;ch đ&uacute;ng đắn, chứ kh&ocirc;ng &ldquo;ủ&rdquo; con qu&aacute; kỹ để rồi tạo ra một thế hệ yếu ớt, kh&ocirc;ng biết tự lập. Những người đi l&agrave;m nhiều năm đọc s&aacute;ch cũng c&oacute; thể nh&igrave;n lại con đường đi của m&igrave;nh, tự ngẫm đ&oacute; đ&atilde; phải l&agrave; con đường m&igrave;nh muốn đi chưa, bởi thay đổi kh&ocirc;ng bao giờ l&agrave; qu&aacute; muộn.</p>\r\n', 1, 1, 3, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(53, 'Chinh Phục Không Gian - Hành Trình Kỳ Diệu', '1f3cf39c8f8425689efc75d657dafd41.jpg', 1, 30000, 'Chinh phục không gian có lẽ là cuộc chinh phục kì diệu và vĩ đại nhất mà con người đã và đang thực hiện. Khám phá vũ trụ bao la là ước mơ từ hàng ngàn năm nhưng cho đến thế kỷ thứ 20 nó mới trở nên sôi động hơn bao giờ hết. Nga – Mỹ hai cường quốc đã tạo ', '<p>Chinh phục kh&ocirc;ng gian c&oacute; lẽ l&agrave; cuộc chinh phục k&igrave; diệu v&agrave; vĩ đại nhất m&agrave; con người đ&atilde; v&agrave; đang thực hiện. Kh&aacute;m ph&aacute; vũ trụ bao la l&agrave; ước mơ từ h&agrave;ng ng&agrave;n năm nhưng cho đến thế kỷ thứ 20 n&oacute; mới trở n&ecirc;n s&ocirc;i động hơn bao giờ hết. Nga &ndash; Mỹ hai cường quốc đ&atilde; tạo ra một cuộc chạy đua v&agrave;o kh&ocirc;ng gian hết sức ngoạn mục, kết quả l&agrave; h&agrave;ng loạt cột mốc đ&atilde; được tạo ra, đ&aacute;nh dấu những bước tiến hết sức quan trọng trong h&agrave;nh tr&igrave;nh chinh phục kh&ocirc;ng gian của con người.</p>\r\n\r\n<p>Nội dung s&aacute;ch t&aacute;i hiện lại những cột mốc quan trọng trong h&agrave;nh tr&igrave;nh chinh phục kh&ocirc;ng gian của con người&nbsp;: Từ việc chế tạo những con t&agrave;u vũ trụ đầu ti&ecirc;n, lần đầu ti&ecirc;n con người bay v&ograve;ng quanh Tr&aacute;i Đất, lần đầu ti&ecirc;n con người đặt ch&acirc;n l&ecirc;n Mặt Trăng, x&acirc;y dựng trạm vũ trụ quốc tế, &hellip; C&ugrave;ng với việc t&aacute;i hiện lại h&agrave;nh tr&igrave;nh chinh phục kh&ocirc;ng gian cuốn s&aacute;ch c&ograve;n đề cập đến những hiệu ứng t&iacute;ch cực của qu&aacute; tr&igrave;nh chinh phục kh&ocirc;ng gian l&ecirc;n cuộc sống thường ng&agrave;y tr&ecirc;n Tr&aacute;i Đất cũng như những bước đi tiếp theo của con người trong h&agrave;nh tr&igrave;nh chinh phục kh&ocirc;ng gian tương lai.</p>\r\n\r\n<p>Ng&agrave;y nay, với sự ph&aacute;t triển của khoa học kỹ thuật, liệu con người sẽ hiện thực h&oacute;a ước mơ sống tr&ecirc;n sao Hỏa? Con người sẽ ph&aacute;t triển ng&agrave;nh du lịch kh&ocirc;ng gian? Con người sẽ t&igrave;m ra những nền văn minh ngo&agrave;i Tr&aacute;i Đất? V&agrave; sẽ c&ograve;n những cột mốc phi thường n&agrave;o m&agrave; con người sẽ đạt được?</p>\r\n', 1, 1, 7, 2, 2, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(55, 'Nhân Học Đối Diện Với Những Vấn Đề Của Thế Giới Hiện Đại', 'd5692b73e9b47b09261a528ce9a4f982.jpg', 1, 64000, 'Kể từ sau công trình nổi tiếng Race et histoire (Chủng tộc và lịch sử, Huyền Giang dịch) được Hội khoa học lịch sử Việt Nam xuất bản năm 1996, khoảng mười năm trở lại đây có thêm ba công trình của ông được dịch ra tiếng Việt. Claude Lévi-Strauss vẫn là co', '<p>Kể từ sau c&ocirc;ng tr&igrave;nh nổi tiếng Race et histoire (Chủng tộc v&agrave; lịch sử, Huyền Giang dịch) được Hội khoa học lịch sử Việt Nam xuất bản năm 1996, khoảng mười năm trở lại đ&acirc;y c&oacute; th&ecirc;m ba c&ocirc;ng tr&igrave;nh của &ocirc;ng được dịch ra tiếng Việt. Claude L&eacute;vi-Strauss vẫn l&agrave; con khủng long kỳ vĩ của ng&agrave;nh nh&acirc;n học, d&acirc;n tộc học.</p>\r\n\r\n<p>M&ugrave;a xu&acirc;n năm 1986, Claude L&eacute;vi-Strauss (1908-2009) tới Nhật Bản lần thứ tư, theo lời mời của quỹ Foundation Ishizaka, thuyết tr&igrave;nh ba buổi ở Tokyo về chủ đề nh&acirc;n học. &Ocirc;ng chọn ti&ecirc;u đề chung cho ba b&agrave;i giảng l&agrave; L&rsquo;Anthropologie face aux probl&egrave;mes du monde moderne (<strong>Nh&acirc;n học đối diện với những vấn đề của thế giới hiện đại</strong>), đ&oacute; cũng l&agrave; t&ecirc;n cuốn s&aacute;ch n&agrave;y.</p>\r\n\r\n<p>L&rsquo;Anthropologie face aux probl&egrave;mes du monde moderne (xuất bản năm 2011, sau khi Claude L&eacute;vi-Strauss mất) thuộc bộ s&aacute;ch &ldquo;La Librairie du XXIe si&egrave;cle&rdquo; (ra đời năm 1989) do nh&agrave; sử học Maurice Olender chủ tr&igrave;, với hơn 200 đầu s&aacute;ch của c&aacute;c học giả lớn được ấn h&agrave;nh.</p>\r\n\r\n<p>Claude L&eacute;vi-Strauss kh&ocirc;ng ngừng ưu tư về c&aacute;c vấn đề x&atilde; hội, về mối quan hệ giữa chủng tộc, lịch sử v&agrave; văn h&oacute;a - những vấn đề nền tảng trong trước t&aacute;c của &ocirc;ng.&nbsp;</p>\r\n\r\n<p>Trong t&aacute;c phẩm n&agrave;y, &ocirc;ng tập trung b&agrave;n về: 1) Sự c&aacute;o chung của văn h&oacute;a b&aacute; quyền phương T&acirc;y; 2) Ba vấn đề lớn của thế giới đương đại: giới t&iacute;nh, sự ph&aacute;t triển kinh tế v&agrave; tư duy huyền thoại; 3) C&ocirc;ng nhận sự đa dạng văn h&oacute;a: những điều ch&uacute;ng ta học được từ nền văn minh Nhật Bản. Trong khi nhấn mạnh tầm quan trọng của nh&acirc;n học như l&agrave; một &ldquo;chủ nghĩa nh&acirc;n đạo d&acirc;n chủ&rdquo; mới, Claude L&eacute;vi-Strauss khảo vấn về &ldquo;Sự c&aacute;o chung của văn h&oacute;a b&aacute; quyền phương T&acirc;y&rdquo;, về mối quan hệ giữa thuyết tương đối văn h&oacute;a v&agrave; ph&aacute;n x&eacute;t đạo đức. Khi khảo s&aacute;t về những vấn đề của một x&atilde; hội to&agrave;n cầu h&oacute;a, &ocirc;ng khảo vấn cả những thực tiễn kinh tế, c&aacute;c vấn đề li&ecirc;n quan đến thụ tinh nh&acirc;n tạo, mối quan hệ giữa tư duy khoa học v&agrave; tư duy huyền thoại.</p>\r\n\r\n<p>Qua ba chủ đề n&ecirc;u tr&ecirc;n, Claude L&eacute;vi-Strauss thể hiện những lo lắng ưu tư của m&igrave;nh về những mối tương đồng của c&aacute;c h&igrave;nh thức kh&aacute;c nhau của sự &ldquo;b&ugrave;ng nổ tư tưởng&rdquo; v&agrave; sự h&igrave;nh th&agrave;nh chủ nghĩa cực đoan&hellip;</p>\r\n\r\n<p>Nhận x&eacute;t về c&aacute;c c&ocirc;ng tr&igrave;nh&nbsp;<strong>Nh&acirc;n học đối diện với những vấn đề của thế giới hiện đại</strong>, nh&agrave; sử học Maurice Olender viết trong &ldquo;Lời tựa&rdquo; rằng: &ldquo;T&aacute;c phẩm của Claude L&eacute;vi-Strauss được cả thế giới c&ocirc;ng nhận v&agrave; hiện nay đ&atilde; trở th&agrave;nh điểm khảo nghiệm tư tưởng hướng về tương lai.&rdquo;</p>\r\n\r\n<p>Cuốn s&aacute;ch n&agrave;y được xuất bản trong khu&ocirc;n khổ hợp t&aacute;c giữa Omega+ với dự &aacute;n Vietnamica thuộc Hội đồng Nghi&ecirc;n cứu ch&acirc;u &Acirc;u.&nbsp;</p>\r\n\r\n<p><strong>+NHẬN X&Eacute;T:</strong></p>\r\n\r\n<p>&ldquo;Cuốn s&aacute;ch Nh&acirc;n học đối diện với những vấn đề của thế giới hiện đại sẽ l&agrave; m&oacute;n qu&agrave; d&agrave;nh tặng cho c&aacute;c bạn trẻ, c&aacute;c sinh vi&ecirc;n v&agrave; c&aacute;c nh&agrave; nghi&ecirc;n cứu mới v&agrave;o nghề, bắt đầu c&oacute; hứng th&uacute; say m&ecirc; t&igrave;m hiểu về nh&acirc;n học n&oacute;i chung v&agrave; L&eacute;vi-Strauss n&oacute;i ri&ecirc;ng. Đ&acirc;y l&agrave; những kiến thức nhập m&ocirc;n về nh&acirc;n học cũng như t&acirc;m thế của nh&agrave; nh&acirc;n học trước những vấn đề cốt yếu của thế giới hiện đại. Cuốn s&aacute;ch bao qu&aacute;t rất nhiều chuy&ecirc;n ng&agrave;nh, từ tự nhi&ecirc;n đến x&atilde; hội, từ sinh học đến khảo cổ, từ kinh tế hiện đại đến huyền thoại xa xưa. Thậm ch&iacute; dự b&aacute;o của t&aacute;c giả về c&aacute;c đại nạn do c&aacute;c lo&agrave;i vi r&uacute;t l&acirc;y lan cũng được n&oacute;i đến trong cuốn s&aacute;ch nhỏ n&agrave;y. V&agrave; ngay l&uacute;c n&agrave;y đ&acirc;y, ch&uacute;ng ta đang hiểu r&otilde; nhất hiểm nguy v&agrave; hậu quả kh&ocirc;n lường của ch&uacute;ng qua đại dịch to&agrave;n cầu do vi r&uacute;t corona g&acirc;y ra.&nbsp;</p>\r\n\r\n<p>Trong biển rộng tri thức m&agrave; xu hướng to&agrave;n cầu h&oacute;a th&ocirc;ng tin c&oacute; thể đem đến, mỗi ch&uacute;ng ta chỉ c&oacute; thể chọn lọc, nhận diện v&agrave; h&agrave;nh động theo phương c&aacute;ch của ri&ecirc;ng m&igrave;nh nhằm đối diện với những vấn đề của c&aacute; nh&acirc;n v&agrave; x&atilde; hội. X&eacute;t cho c&ugrave;ng, như L&eacute;vi-Strauss đ&atilde; từng kết luận một c&aacute;ch kh&aacute; bi quan, d&ugrave; rất thực tế, rằng con người đến v&agrave; đi đều ở trạng th&aacute;i trần truồng, chẳng thể mang theo được g&igrave; c&ugrave;ng với th&acirc;n x&aacute;c hư n&aacute;t. Kết th&uacute;c cuốn s&aacute;ch L&rsquo;Homme nu (Con người trần truồng, 1971), &ocirc;ng chỉ bu&ocirc;ng th&otilde;ng đ&uacute;ng một từ Rien (kh&ocirc;ng) tức l&agrave; &ldquo;kh&ocirc;ng c&ograve;n g&igrave; cả!&rdquo;. Chỉ độc một từ Rien ấy th&ocirc;i, nhẹ hẫng, bất lực v&agrave; đầy tiếc nuối, như một kiếp người trần trụi vừa h&ograve;a m&igrave;nh v&agrave;o c&otilde;i thinh kh&ocirc;ng&hellip;&rdquo;&nbsp;</p>\r\n\r\n<p><strong>Nguyễn Thị Hiệp&nbsp;</strong></p>\r\n\r\n<p><strong>Viện Khảo cứu Cao cấp Ph&aacute;p (EPHE)</strong></p>\r\n\r\n<p><strong>+TR&Iacute;CH DẪN:</strong></p>\r\n\r\n<p>&ldquo;Những g&igrave; m&agrave; c&aacute;i nh&igrave;n của ch&uacute;ng t&ocirc;i, những người phương T&acirc;y kh&aacute;c, chiếu v&agrave;o Nhật Bản khẳng định rằng mỗi nền văn h&oacute;a l&agrave; đặc th&ugrave; v&agrave; to&agrave;n bộ c&aacute;c nền văn h&oacute;a, m&agrave; từ đ&oacute; lo&agrave;i người được tạo n&ecirc;n, kh&ocirc;ng thể chỉ tồn tại v&agrave; hưng thịnh bằng c&aacute;ch vận h&agrave;nh theo một nhịp độ song song l&uacute;c mở, l&uacute;c đ&oacute;ng, l&uacute;c bị lệch pha, khi th&igrave; tr&ugrave;ng khớp trong độ d&agrave;i thời gian. Để giữ t&iacute;nh độc đ&aacute;o v&agrave; đứng vững trước c&aacute;c nền văn h&oacute;a kh&aacute;c, những sự kh&aacute;c biệt c&oacute; thể l&agrave;m phong ph&uacute; th&ecirc;m cho nhau, tất cả mọi nền văn h&oacute;a cần phải được l&agrave; ch&iacute;nh m&igrave;nh, c&aacute;i gi&aacute; phải trả cho việc l&agrave; ch&iacute;nh m&igrave;nh l&agrave; sự c&acirc;m điếc trước những gi&aacute; trị kh&aacute;c nhau một c&aacute;ch to&agrave;n cục hay bộ phận.&rdquo;</p>\r\n\r\n<p>&ldquo;Nh&acirc;n học dạy cho ch&uacute;ng ta biết mỗi phong tục, mỗi t&iacute;n ngưỡng đối với ch&uacute;ng ta c&oacute; vẻ lập dị, phi l&yacute; khi so s&aacute;nh với những phong tục v&agrave; t&iacute;n ngưỡng của ch&uacute;ng ta, nhưng ch&uacute;ng lại l&agrave; một bộ phận trong một hệ thống m&agrave; sự c&acirc;n bằng nội tại được h&igrave;nh th&agrave;nh qua h&agrave;ng thế kỷ, v&agrave; từ chỉnh thể n&agrave;y, ta c&oacute; thể triệt ti&ecirc;u một yếu tố m&agrave; kh&ocirc;ng c&oacute; nguy cơ ph&aacute; hủy phần c&ograve;n lại. Ngay cả khi nh&acirc;n học kh&ocirc;ng mang lại những th&ocirc;ng tin kh&aacute;c th&igrave; chỉ yếu tố n&agrave;y th&ocirc;i cũng đủ để c&ocirc;ng nhận vị tr&iacute; ng&agrave;y c&agrave;ng quan trọng của chuy&ecirc;n ng&agrave;nh n&agrave;y trong c&aacute;c ng&agrave;nh khoa học x&atilde; hội v&agrave; nh&acirc;n văn.&rdquo;</p>\r\n', 1, 1, 7, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(56, 'Đề Thi Đẫm Máu ', 'a195fe8af22c6d529681eaaaf7de31ac.jpg', 1, 116000, 'Một tên sát thủ có sở thích uống chất hỗn hợp máu nạn nhân với sữa tươi, hắn có căn bệnh gì đặc biệt hay là con quỷ hút máu bất tử nghìn năm trong truyền thuyết?\r\n\r\nTrong thành phố C liên tiếp xảy ra 4 vụ cưỡng hiếp giết người, nạn nhân đều là những cô gá', '<p>Một t&ecirc;n s&aacute;t thủ c&oacute; sở th&iacute;ch uống chất hỗn hợp m&aacute;u nạn nh&acirc;n với sữa tươi, hắn c&oacute; căn bệnh g&igrave; đặc biệt hay l&agrave; con quỷ h&uacute;t m&aacute;u bất tử ngh&igrave;n năm trong truyền thuyết?</p>\r\n\r\n<p>Trong th&agrave;nh phố C li&ecirc;n tiếp xảy ra 4 vụ cưỡng hiếp giết người, nạn nh&acirc;n đều l&agrave; những c&ocirc; g&aacute;i tr&iacute; thức từ 25 - 35 tuổi, đ&acirc;y rốt cuộc l&agrave; giết người trả th&ugrave; hay đơn giản l&agrave; cưỡng d&acirc;m?</p>\r\n\r\n<p>H&agrave;ng loạt c&aacute;i chết b&iacute; ẩn thảm khốc của những người sống trong trường Đại học J li&ecirc;n tiếp xảy ra. Ở hiện trường vụ &aacute;n, hung thủ đều để lại gợi &yacute; cho vụ &aacute;n tiếp theo, nhằm gợi &yacute; g&igrave;?</p>\r\n\r\n<p>Trong h&agrave;ng loạt c&aacute;c vụ &aacute;n ly kỳ khiến cảnh s&aacute;t b&agrave;ng ho&agrave;ng b&oacute; tay, Phương Mộc trầm mặc kiệm lời đột nhi&ecirc;n bị cảnh s&aacute;t l&ocirc;i v&agrave;o cuộc. T&ecirc;n &aacute;c quỷ giấu mặt lần lượt giết hại những người bạn của cậu, v&igrave; sao? Khi c&acirc;u trả lời được v&eacute;n m&agrave;n b&iacute; mật, th&igrave; đề thi t&agrave;n khốc đ&atilde; bị t&iacute;ch 5 dấu X đẫm m&aacute;u.</p>\r\n\r\n<p>Một cuộc đấu tr&iacute; so t&agrave;i khốc liệt đầy kịch t&iacute;nh nổ Ai sẽ l&agrave; người thắng cuộc?</p>\r\n\r\n<p>Đ&acirc;y l&agrave; t&aacute;c phẩm trinh th&aacute;m được nhiều người biết đến nhất của L&ocirc;i Mễ - sĩ quan cảnh s&aacute;t cấp ph&ograve;ng (sở) giảng dạy bộ m&ocirc;n H&igrave;nh ph&aacute;p học tại một trường cảnh s&aacute;t trực thuộc Bộ C&ocirc;ng an Trung Quốc, đồng thời cũng l&agrave; một t&aacute;c giả truyện trinh th&aacute;m nổi tiếng. Truyện của L&ocirc;i Mễ lu&ocirc;n c&oacute; sức hấp dẫn đặc biệt đối với độc giả bởi ng&ograve;i b&uacute;t sắt b&eacute;n với những t&igrave;nh tiết ly k&igrave;, l&ocirc;i cuốn đến trang cuối c&ugrave;ng.</p>\r\n', 1, 1, 9, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(57, 'GOTH - Những Kẻ Hắc Ám', '5e23fe4baa8c7d40bfbf58d6ff609950.jpg', 1, 77000, 'Một cuốn sổ ghi chép quá trình giết người.Chiếc tủ lạnh chứa đầy bàn tay. Lũ chó bị bắt cóc.Vụ treo cổ kỳ quái.Đứa trẻ bị chôn sống. Cuốn băng thu âm giọng nói của người chết…\r\n\r\nHai học sinh cấp ba cùng nhau điều tra những vụ án kỳ lạ ở địa phương. Nhưng', '<p>Một cuốn sổ ghi ch&eacute;p qu&aacute; tr&igrave;nh giết người.Chiếc tủ lạnh chứa đầy b&agrave;n tay. Lũ ch&oacute; bị bắt c&oacute;c.Vụ treo cổ kỳ qu&aacute;i.Đứa trẻ bị ch&ocirc;n sống. Cuốn băng thu &acirc;m giọng n&oacute;i của người chết&hellip;</p>\r\n\r\n<p>Hai học sinh cấp ba c&ugrave;ng nhau điều tra những vụ &aacute;n kỳ lạ ở địa phương. Nhưng thay v&igrave; cố gắng ngăn chặn tội &aacute;c, nỗi &aacute;m ảnh với c&aacute;i chết v&agrave; sự giết ch&oacute;c đ&atilde; dẫn dắt ch&uacute;ng tiến v&agrave;o b&oacute;ng tối đi&ecirc;n cuồng, nơi những cơn &aacute;c mộng trở th&agrave;nh sự thực.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ l&agrave; một cuốn tiểu thuyết kinh dị r&ugrave;ng rợn, GOTH c&ograve;n ẩn chứa nỗi buồn v&agrave; những chi&ecirc;m nghiệm về cuộc sống, về bản chất con người v&agrave; sự gi&aacute;c ngộ. T&aacute;c phẩm đ&atilde; mang về cho Otsuichi giải thưởng Honkaku Mystery, được chuyển thể th&agrave;nh manga v&agrave; phim điện ảnh.</p>\r\n', 1, 1, 9, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(58, 'Làm Sạch Mạch Máu - Những Tác Phẩm Kinh Điển Của Nền Y Học Tự Nhiên', '72d59c232b950777c4bb3246045a2873.jpg', 1, 45000, 'Nhiều bạn đọc Việt Nam - những độc giả mến trọng của tôi, có thể là đang có trong nhà mình, hoặc đang đọc, hoặc đang trải nghiệm những điều nào đó trong cuốn sách nhỏ “Những phương thức phục hồi sức khỏe theo tự nhiên” của Thầy Nishi Katsuzo. Trong dòng c', '<p>Nhiều bạn đọc Việt Nam - những độc giả mến trọng của t&ocirc;i, c&oacute; thể l&agrave; đang c&oacute; trong nh&agrave; m&igrave;nh, hoặc đang đọc, hoặc đang trải nghiệm những điều n&agrave;o đ&oacute; trong cuốn s&aacute;ch nhỏ &ldquo;<strong>Những phương thức phục hồi sức khỏe theo tự nhi&ecirc;n</strong>&rdquo; của Thầy Nishi Katsuzo. Trong d&ograve;ng chảy tr&agrave;n đầy năng lượng mạnh mẽ của y&ecirc;u thương đang d&acirc;ng tr&agrave;o từ tr&aacute;i tim; trong niềm kh&aacute;t khao m&atilde;nh liệt muốn được cung cấp th&ecirc;m những th&ocirc;ng tin, t&ocirc;i cho l&agrave;, kh&aacute; quan trọng, kh&aacute; cấp thiết v&agrave; lu&ocirc;n l&agrave; thời sự, đến tất cả những ai đang rất quan t&acirc;m trong cuộc sống đương đại: Liệu c&oacute; phương c&aacute;ch n&agrave;o gi&uacute;p ch&uacute;ng ta tr&aacute;nh được những căn bệnh hiểm ngh&egrave;o ng&agrave;y c&agrave;ng gia tăng, kh&ocirc;ng chỉ ở những người cao tuổi, m&agrave; ngay cả những người c&ograve;n kh&aacute; trẻ - đang độ tuổi cống hiến nhiều nhất? Thủ phạm của c&aacute;c căn bệnh như nhồi m&aacute;u cơ tim, tai biến mạch m&aacute;u n&atilde;o, liệt nửa người, thậm ch&iacute; chết &ldquo;bất đắc kỳ tử&rdquo;&hellip; l&agrave; g&igrave;? Những sự ra đi qu&aacute; đường đột&hellip; đ&atilde; để lại những nỗi đau x&eacute; ruột&hellip;!!! Mới 3 ng&agrave;y trước đ&acirc;y t&ocirc;i vừa hẹn qua điện thoại, sẽ tặng bạn t&ocirc;i một cuốn s&aacute;ch t&ocirc;i vừa dịch về sức khỏe&hellip; v&agrave; bạn t&ocirc;i cũng hẹn sẽ tặng t&ocirc;i một tập thơ m&agrave; bạn t&ocirc;i vừa xuất bản&hellip; Ch&uacute;ng t&ocirc;i chưa kịp trao cho nhau những m&oacute;n qu&agrave; giản dị để sẻ chia&hellip; th&igrave; được tin bạn t&ocirc;i đ&atilde; ra đi một m&igrave;nh, đột ngột trong đ&ecirc;m kh&ocirc;ng một ai biết&hellip;!!! V&agrave; thế l&agrave; cả hai ch&uacute;ng t&ocirc;i đ&atilde; trở th&agrave;nh người mắc nợ lẫn nhau! V&agrave; t&ocirc;i th&igrave; trong suốt cuộc đời m&igrave;nh, cho đến h&ocirc;m nay, từ s&acirc;u thẳm tr&aacute;i tim m&igrave;nh kh&ocirc;ng muốn mắc nợ ai! Nhưng t&ocirc;i cũng kh&ocirc;ng d&aacute;m chắc l&agrave; t&ocirc;i đ&atilde; kh&ocirc;ng nợ nần ai. Bằng chứng l&agrave; trong những ng&agrave;y n&agrave;y, t&ocirc;i kh&ocirc;ng dấu nổi những suy nghĩ th&ocirc;i th&uacute;c: &ldquo;phải cố gắng l&agrave;m sao để c&oacute; thể nhanh nhất ho&agrave;n tất cuốn s&aacute;ch dịch thứ hai: &ldquo;<strong>L&agrave;m sạch mạch v&agrave; m&aacute;u</strong>&rdquo;&rdquo; trong số 4 cuốn s&aacute;ch m&agrave; t&ocirc;i đ&atilde; được gia đ&igrave;nh Thầy Nishi Katsuzo cho bản quyền t&aacute;c giả xuất bản tại Việt Nam, để nhiều người c&ugrave;ng được đọc.</p>\r\n\r\n<p>Một lần nữa - lần thứ 2, t&ocirc;i xin tr&acirc;n trọng k&iacute;nh chuyển tới c&aacute;c bạn đọc k&iacute;nh trọng của t&ocirc;i l&ograve;ng mong muốn v&agrave; niềm tin chắc chắn rằng, cuốn s&aacute;ch dịch nhỏ thứ hai n&agrave;y, c&oacute; thể sẽ cung cấp th&ecirc;m những th&ocirc;ng tin mới đầy ắp t&iacute;nh khoa học, t&iacute;nh thiết thực đối với việc giữ g&igrave;n sức khỏe cộng đồng. Hy vọng n&oacute; sẽ trở th&agrave;nh người bạn gần gũi, đ&aacute;ng tin cậy, lu&ocirc;n đồng h&agrave;nh c&ugrave;ng c&aacute;c bạn trong cuộc sống hiện tại. C&ugrave;ng với cuốn &ldquo;Những phương thức phục hồi sức khỏe theo tự nhi&ecirc;n&rdquo;, c&oacute; thể n&oacute; sẽ cho c&aacute;c bạn th&ecirc;m chỗ dựa vững chắc để c&aacute;c bạn nương theo, m&agrave; vận dụng s&aacute;ng tạo ph&ugrave; hợp với điều kiện cụ thể trong việc chăm s&oacute;c sức khỏe cho ch&iacute;nh bạn, c&ugrave;ng người th&acirc;n của m&igrave;nh sao lu&ocirc;n giữ cho m&igrave;nh cả sức khỏe th&acirc;n x&aacute;c lẫn t&acirc;m hồn trong s&aacute;ng, thanh tao.</p>\r\n\r\n<p>T&ocirc;i th&agrave;nh t&acirc;m hy vọng những trang s&aacute;ch n&agrave;y sẽ đưa c&aacute;c bạn thẳng tới những chỉ dẫn cụ thể về việc ăn, uống, tập luyện th&acirc;n thể v&agrave; suy nghĩ nhằm gi&uacute;p c&aacute;c bạn nhanh ch&oacute;ng &ldquo;L&agrave;m sạch mạch v&agrave; m&aacute;u&rdquo; - thứ m&agrave; Thầy Nishi Katsuzo cho l&agrave; nguy&ecirc;n nh&acirc;n cơ bản nhất v&agrave; trực tiếp gi&uacute;p ta đẩy l&ugrave;i c&aacute;c bệnh nguy hiểm như đ&atilde; n&ecirc;u.</p>\r\n\r\n<p>Nishi Katsuzo đ&atilde; đưa ra c&aacute;c giải th&iacute;ch khoa học (v&iacute; dụ: chỉ ri&ecirc;ng việc ăn uống) rằng thức ăn đưa v&agrave;o cơ thể cần phải gồm những loại n&agrave;o để hội đủ 4 nguy&ecirc;n tố cấu th&agrave;nh n&ecirc;n cơ thể con người. Đ&oacute; l&agrave; LỬA (&aacute;nh s&aacute;ng mặt trời) l&agrave; KH&Ocirc;NG KH&Iacute;, l&agrave; ĐẤT v&agrave; NƯỚC. T&aacute;c giả cũng đ&atilde; khẳng định (ngay trong cuốn s&aacute;ch) l&agrave; cứ qua mỗi 10 ng&agrave;y một, khi cơ thể được nu&ocirc;i dưỡng đ&uacute;ng sẽ nhận được m&aacute;u mới ho&agrave;n to&agrave;n tốt, v&agrave; cơ thể sẽ tự động điều chỉnh đ&aacute;ng kể về sức khỏe theo hướng t&iacute;ch cực. C&aacute;c chỉ dẫn của &ocirc;ng rất r&otilde; r&agrave;ng, dễ hiểu. C&aacute;c nguồn thức ăn m&agrave; &ocirc;ng giới thiệu đ&acirc;u c&oacute; đắt đỏ v&agrave; kh&oacute; kiếm t&igrave;m trong thực tế đời sống của ch&uacute;ng ta. C&ograve;n lợi &iacute;ch của n&oacute;, c&aacute;c bạn h&atilde;y c&ugrave;ng trải nghiệm v&agrave; tự c&aacute;c bạn sẽ c&oacute; c&acirc;u trả lời!</p>\r\n', 1, 1, 4, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(59, 'Toàn Cảnh Dinh Dưỡng - Thức Tỉnh Và Hành Động', '9e3b71e05a5fa7bc4a31d8814be1c41d.jpg', 1, 30000, 'WHOLE - TOÀN CẢNH DINH DƯỠNG THỨC TỈNH VÀ HÀNH ĐỘNG\r\n\r\nTheo thống kê mới nhất năm 2018 của Tổ chức Y tế thế giới WHO. Hiện nay, ở Việt Nam mỗi năm có 115.000 người chết vì ung thư, 200.000 người chết vì bệnh tim mạch, 3.5 triệu người bị bệnh tiểu đường. Đ', '<p><strong>WHOLE - TO&Agrave;N CẢNH DINH DƯỠNG THỨC TỈNH V&Agrave; H&Agrave;NH ĐỘNG</strong></p>\r\n\r\n<p>Theo thống k&ecirc; mới nhất năm 2018 của Tổ chức Y tế thế giới WHO. Hiện nay, ở Việt Nam mỗi năm c&oacute; 115.000 người chết v&igrave; ung thư, 200.000 người chết v&igrave; bệnh tim mạch, 3.5 triệu người bị bệnh tiểu đường. Điều đ&aacute;ng n&oacute;i, số ca mắc bệnh mới gia tăng kh&ocirc;ng ngừng v&agrave; độ tuổi bị bệnh ng&agrave;y c&agrave;ng trẻ hơn.</p>\r\n\r\n<p>Nếu bạn muốn sống một cuộc đời kh&ocirc;ng phải ưu lo về ung thư, bệnh tim, tiểu đường,... th&igrave; tất cả những g&igrave; cần l&agrave;m đều đ&atilde; nằm trong tay bạn: &ldquo;To&agrave;n cảnh dinh dưỡng &ndash; Thức tỉnh v&agrave; h&agrave;nh động&rdquo;.&nbsp;Đ&acirc;y l&agrave;&nbsp;cuốn s&aacute;ch nổi tiếng khắp to&agrave;n cầu của Tiến sĩ Y khoa T.Colin Campbell &ndash; &Ocirc;ng hiện l&agrave; gi&aacute;o sư dinh dưỡng ở Đại học Cornell, Hoa Kỳ v&agrave; l&agrave; c&aacute;nh chim đầu đ&agrave;n trong lĩnh vực nghi&ecirc;n cứu dinh dưỡng tr&ecirc;n thế giới hơn nữa thế kỷ qua.</p>\r\n\r\n<p>Khi đến với &ldquo;Whole: To&agrave;n cảnh dinh dưỡng &ndash; Thức tỉnh v&agrave; h&agrave;nh động&rdquo;, c&aacute;c bạn sẽ t&igrave;m thấy:</p>\r\n\r\n<ul>\r\n	<li><strong><em>Thứ nhất</em>: dinh dưỡng l&agrave; ch&igrave;a kh&oacute;a ch&iacute;nh yếu đối với sức khỏe con người;</strong></li>\r\n	<li><strong><em>Thứ hai</em>: điều m&agrave; hầu hết ch&uacute;ng ta xem như dinh dưỡng hợp l&yacute; th&igrave; h&oacute;a ra kh&ocirc;ng phải l&agrave; như vậy;</strong></li>\r\n	<li><strong><em>Thứ ba:</em>&nbsp;v&eacute;n m&agrave;n những b&iacute; mật động trời về dinh dưỡng đ&atilde; được che giấu hơn nữa thế kỷ qua</strong></li>\r\n</ul>\r\n', 1, 1, 4, 2, 2, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(60, 'ISMS - Hiểu Về Nghệ Thuật Hiện Đại', '8164445fad71288bc490a108e020c043.jpg', 1, 196000, 'Nghệ thuật hiện đại, hơn bất kỳ đề tài nào khác, luôn được cấu trúc bởi các ism: các phong trào, các khuynh hướng, các phong cách hoặc các trường phái hoạt động như những loại hình thực hành nghệ thuật của các nghệ sĩ khác nhau. Cuốn sách cho chúng ta thấ', '<p>Nghệ thuật hiện đại, hơn bất kỳ đề t&agrave;i n&agrave;o kh&aacute;c, lu&ocirc;n được cấu tr&uacute;c bởi c&aacute;c ism: c&aacute;c phong tr&agrave;o, c&aacute;c khuynh hướng, c&aacute;c phong c&aacute;ch hoặc c&aacute;c trường ph&aacute;i hoạt động như những loại h&igrave;nh thực h&agrave;nh nghệ thuật của c&aacute;c nghệ sĩ kh&aacute;c nhau. Cuốn s&aacute;ch cho ch&uacute;ng ta thấy rằng nghệ thuật hiện đại v&agrave; hậu hiện đại thay đổi qu&aacute; nhanh ch&oacute;ng, kh&aacute;c hẳn nghệ thuật h&agrave;n l&acirc;m v&agrave; thời kỳ phục hưng.</p>\r\n\r\n<p>Bởi v&igrave;, bắt đầu từ cuối thế kỷ 19, nhiều nghệ sĩ đ&atilde; nỗ lực tạo ra c&aacute;c trường ph&aacute;i để tự định nghĩa ch&iacute;nh m&igrave;nh v&agrave; đi k&egrave;m l&agrave; c&aacute;c tuy&ecirc;n ng&ocirc;n để l&agrave;m r&otilde; điều g&igrave; đ&atilde; tạo n&ecirc;n sự kh&aacute;c biệt về t&aacute;c phẩm của họ. Khi t&aacute;c phẩm được ph&acirc;n loại, c&ocirc;ng ch&uacute;ng v&agrave; những nh&agrave; ph&ecirc; b&igrave;nh nghệ thuật sẽ biết tới ch&uacute;ng nhiều hơn. V&agrave; trường ph&aacute;i Ấn tượng đ&atilde; mang đến cuộc tranh c&atilde;i lớn trong lịch sử nghệ thuật n&oacute;i chung v&agrave; c&aacute;ch định nghĩa nghệ thuật n&oacute;i ri&ecirc;ng trong giới nghệ sĩ.</p>\r\n\r\n<p>Từ đ&oacute; đến nay, ch&uacute;ng ta đ&atilde; chứng kiến v&ocirc; số tr&agrave;o lưu nghệ thuật được h&igrave;nh th&agrave;nh, những trường ph&aacute;i được thực h&agrave;nh, thậm ch&iacute; cả những chủ nghĩa nh&acirc;n văn ăn s&acirc;u v&agrave;o đời sống nghệ thuật, văn h&oacute;a v&agrave; tinh thần của kh&ocirc;ng chỉ giới họa sĩ m&agrave; cả văn nghệ sĩ n&oacute;i chung.</p>\r\n\r\n<p>Cuốn s&aacute;ch n&agrave;y l&agrave; một cẩm nang ngắn gọn giới thiệu cho bạn đọc về 55 tr&agrave;o lưu, trường ph&aacute;i v&agrave; phong c&aacute;ch thịnh h&agrave;nh từ thời kỳ b&igrave;nh minh h&eacute; s&aacute;ng của nghệ thuật hiện đại cuối thế kỷ 19 cho đến nay, nhằm gi&uacute;p c&aacute;c bạn l&agrave;m quen v&agrave; tiếp cận được một nh&aacute;nh của d&ograve;ng chảy lịch sử nghệ thuật.</p>\r\n\r\n<p>Mỗi trường ph&aacute;i sẽ được giới thiệu c&aacute;c đề mục cơ bản: giới thiệu tổng qu&aacute;t, c&aacute;c nghệ sĩ ch&iacute;nh, c&aacute;c thuật ngữ ch&iacute;nh, định nghĩa chủ đạo, c&aacute;c t&aacute;c phẩm điển h&igrave;nh, c&aacute;c t&aacute;c phẩm kh&aacute;c, c&aacute;c trường ph&aacute;i tương tự hoặc đối nghịch.</p>\r\n\r\n<p><strong>Đ&aacute;nh gi&aacute;/ Nhận x&eacute;t của chuy&ecirc;n gia:</strong></p>\r\n\r\n<p>_ &ldquo; giống như một lời giới thiệu về một đất nước xa lạ &ndash; với lịch sử, thời kỳ v&agrave; văn h&oacute;a của ri&ecirc;ng n&oacute;. Như một hướng dẫn vi&ecirc;n du lịch chỉ ra những điểm nổi bật của một th&agrave;nh phố chưa từng được biết đến trước đ&acirc;y, Phillips đưa ch&uacute;ng ta đi qua những tr&agrave;o lưu lớn v&agrave; nhỏ của d&ograve;ng chảy nghệ thuật, bối cảnh h&oacute;a ch&uacute;ng th&agrave;nh một lịch sử trải d&agrave;i từ đầu thế kỷ 19 đến ng&agrave;y nay.&rdquo;</p>\r\n\r\n<p><strong>Về t&aacute;c giả:</strong></p>\r\n\r\n<p>Sam Phillips l&agrave; một nh&agrave; b&aacute;o, t&aacute;c giả v&agrave; bi&ecirc;n tập vi&ecirc;n chuy&ecirc;n về lĩnh vực nghệ thuật, hiện đang sống ở London. &Ocirc;ng l&agrave; bi&ecirc;n tập vi&ecirc;n của Tạp ch&iacute; RA Magazine, được xuất bản bởi Học viện Nghệ thuật Ho&agrave;ng gia. C&aacute;c lĩnh vực sở trường của &ocirc;ng gồm c&oacute; nghệ thuật, thiết kế, kiến tr&uacute;c v&agrave; &acirc;m nhạc, với nhiều b&agrave;i viết đ&oacute;ng g&oacute;p cho những ấn phẩm nổi tiếng như c&aacute;c Tạp ch&iacute; The World of Interiors, I-D Magazine, Artists &amp; Illustrators, Asian Art Newspaper, v&agrave; Thời b&aacute;o The Independent.</p>\r\n\r\n<p><strong>C&aacute;c t&aacute;c phẩm nổi bật:</strong></p>\r\n\r\n<p>The Art Guide: London (2011)</p>\r\n\r\n<p>Understanding Modern Art (2012)</p>\r\n', 1, 1, 10, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(61, 'Câu Chuyện Nghệ Thuật', 'a1866ce83f89de4b846e3630d653aa9a.jpg', 1, 17000, 'Câu chuyện nghệ thuật - cẩm nang về các trào lưu, các tác phẩm, chủ đề và kĩ thuật chính yếu trong nghệ thuật.\r\n\r\nCuốn sách là một bước dạo đầu mới lạ vào chủ đề nghệ thuật. Với kết cấu đơn giản, cuốn sách này điểm qua 50 tác phẩm then chốt, từ các bức vẽ', '<p>C&acirc;u chuyện nghệ thuật - cẩm nang về c&aacute;c tr&agrave;o lưu, c&aacute;c t&aacute;c phẩm, chủ đề v&agrave; kĩ thuật ch&iacute;nh yếu trong nghệ thuật.</p>\r\n\r\n<p>Cuốn s&aacute;ch l&agrave; một bước dạo đầu mới lạ v&agrave;o chủ đề nghệ thuật. Với kết cấu đơn giản, cuốn s&aacute;ch n&agrave;y điểm qua 50 t&aacute;c phẩm then chốt, từ c&aacute;c bức vẽ tr&ecirc;n v&aacute;ch động Lascaux tới những t&aacute;c phẩm sắp đặt đương đại, v&agrave; li&ecirc;n hệ c&aacute;c t&aacute;c phẩm ấy với những tr&agrave;o lưu, chủ đề c&ugrave;ng kĩ thuật ch&iacute;nh yếu trong nghệ thuật.</p>\r\n\r\n<p>Dễ tiếp cận, s&uacute;c t&iacute;ch v&agrave; gi&agrave;u minh họa, C&acirc;u chuyện nghệ thuật sẽ giải th&iacute;ch nghệ thuật đ&atilde; thay đổi v&igrave; đ&acirc;u, v&agrave;o thời điểm n&agrave;o, như thế n&agrave;o; những ai ti&ecirc;n phong s&aacute;ng tạo c&aacute;c &yacute; niệm, c&aacute;c &yacute; niệm ấy c&oacute; khởi nguồn, bối cảnh th&agrave;nh h&igrave;nh v&agrave; tầm quan trọng ra sao. Cuốn s&aacute;ch n&agrave;y sẽ l&iacute; giải tường minh c&aacute;c thuật ngữ nghệ thuật, cho độc giả kiến thức bao qu&aacute;t v&agrave; sự am hiểu thấu triệt để thưởng thức nghệ thuật qua c&aacute;c thời k&igrave;.</p>\r\n\r\n<p><strong>Th&ocirc;ng tin t&aacute;c giả:</strong></p>\r\n\r\n<p>Susie Hodge l&agrave; một nh&agrave; sử học nghệ thuật, một sử gia v&agrave; một họa sĩ. C&ocirc; l&agrave; t&aacute;c giả ăn kh&aacute;ch với nhiều tựa s&aacute;ch như 50 Art Ideas (50 &Yacute; niệm nghệ thuật), How to Survive Modern Art (Vượt qua th&aacute;ch thức của Nghệ thuật Hiện đại), Why Your Five Year Old Could Not Have Done That: Modern Art Explained (L&iacute; do tuyệt phẩm kh&ocirc;ng ra đời dưới b&agrave;n tay đứa trẻ l&ecirc;n năm: L&iacute; giải Nghệ thuật Hiện đại) v&agrave; 50 Art Nouveau Works of Art You Should Know (50 t&aacute;c phẩm T&acirc;n Nghệ thuật n&ecirc;n biết).</p>\r\n', 1, 1, 3, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(62, 'Van Gogh', '8bbc90b1e298a2202bfabbdd9d5d630a.jpg', 1, 131000, 'Cuốn sách, mà ta có thể đọc say mê như một tiểu thuyết, kể lại, với những chi tiết không giải thích gì mà nói lên tất cả, quá trình từ bỏ con đường giáo sĩ để tận hiến cho hội họa của Vincent, tiến diễn hội họa của ông, tình anh em cảm động đến xót xa giữ', '<p>Cuốn s&aacute;ch, m&agrave; ta c&oacute; thể đọc say m&ecirc; như một tiểu thuyết, kể lại, với những chi tiết kh&ocirc;ng giải th&iacute;ch g&igrave; m&agrave; n&oacute;i l&ecirc;n tất cả, qu&aacute; tr&igrave;nh từ bỏ con đường gi&aacute;o sĩ để tận hiến cho hội họa của Vincent, tiến diễn hội họa của &ocirc;ng, t&igrave;nh anh em cảm động đến x&oacute;t xa giữa Vincent v&agrave; Th&eacute;o, mối quan hệ đầy kh&uacute;c mắc với Gauguin, những cơn bệnh t&acirc;m thần, những ảo gi&aacute;c, thời kỳ điều trị nội tr&uacute; ở bệnh viện Saint-R&eacute;my v&agrave; cuối c&ugrave;ng, ph&aacute;t s&uacute;ng tự bắn v&agrave;o ngực giữa c&aacute;nh đồng l&uacute;a m&igrave; một chiều th&aacute;ng 7 năm 1890, mặc d&ugrave; kh&ocirc;ng một khẩu s&uacute;ng n&agrave;o được t&igrave;m thấy ở hiện trường.</p>\r\n', 1, 1, 10, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00');
INSERT INTO `product` (`id`, `name`, `image`, `quantity`, `price`, `decription`, `detail`, `status`, `featured`, `id_productCategory`, `id_Author`, `id_Publisher`, `created_at`, `updated_at`) VALUES
(63, 'Claude Monet', '8827bb11dd5ab172b33fa248a4c21148.jpg', 1, 224000, 'Không một nghệ sĩ thế kỷ XX nào không dành lòng kính trọng và tôn vinh tới Claude Monet, nhà tiên phong, người dẫn dắt phong trào nghệ thuật lớn nhất thời đại của mình – hội họa Ấn tượng. Qua những phân tích và minh họa về 45 kiệt tác lớn nhất của Claude ', '<p>Kh&ocirc;ng một nghệ sĩ thế kỷ XX n&agrave;o kh&ocirc;ng d&agrave;nh l&ograve;ng k&iacute;nh trọng v&agrave; t&ocirc;n vinh tới Claude Monet, nh&agrave; ti&ecirc;n phong, người dẫn dắt phong tr&agrave;o nghệ thuật lớn nhất thời đại của m&igrave;nh &ndash; hội họa Ấn tượng. Qua những ph&acirc;n t&iacute;ch v&agrave; minh họa về 45 kiệt t&aacute;c lớn nhất của Claude Monet, ch&uacute;ng ta sẽ hiểu v&igrave; sao cho đến tận ng&agrave;y nay, &ocirc;ng vẫn nằm trong số c&aacute;c danh họa được c&ocirc;ng ch&uacute;ng y&ecirc;u th&iacute;ch nhất.</p>\r\n\r\n<p>Với mỗi t&aacute;c phẩm xuất sắc được giới thiệu trong cuốn s&aacute;ch n&agrave;y, h&atilde;y kh&aacute;m ph&aacute;:</p>\r\n\r\n<ul>\r\n	<li>Những l&yacute; do khiến n&oacute; trở th&agrave;nh một kiệt t&aacute;c &ldquo;kh&ocirc;ng thể chối c&atilde;i&rdquo;;</li>\r\n	<li>Những ho&agrave;n cảnh s&aacute;ng t&aacute;c, nơi chốn v&agrave; ng&agrave;y th&aacute;ng thực hiện;</li>\r\n	<li>Những giải th&iacute;ch về kỹ thuật vẽ m&agrave; họa sĩ sử dụng;</li>\r\n	<li>Những ch&igrave;a kh&oacute;a để hiểu điều m&agrave; nghệ sĩ muốn thể hiện,</li>\r\n	<li>Những chi tiết về k&iacute;ch thước, phương tiện, chất liệu v&agrave; nơi lưu giữ,</li>\r\n	<li>Những lời tr&iacute;ch dẫn gợi ra tầm quan trọng của t&aacute;c phẩm trong lịch sử nghệ thuật.</li>\r\n</ul>\r\n\r\n<p>Với lối tr&igrave;nh b&agrave;y r&otilde; r&agrave;ng, mạch lạc, chia l&agrave;m hai phần: cuộc đời danh họa v&agrave; những kiệt t&aacute;c, cuốn s&aacute;ch sẽ d&agrave;nh cho những người y&ecirc;u th&iacute;ch danh họa Claude Monet v&agrave; muốn t&igrave;m hiểu, tra cứu th&ocirc;ng tin nhanh ch&oacute;ng về cuộc đời &ocirc;ng, c&ugrave;ng 45 kiệt t&aacute;c m&agrave; &ocirc;ng đ&atilde; để lại.</p>\r\n\r\n<p>Cuốn s&aacute;ch nằm trong bộ s&aacute;ch về c&aacute;c t&aacute;c giả v&agrave; t&aacute;c phẩm hội họa nổi tiếng, thuộc tủ s&aacute;ch nghệ thuật của Omega+, mua bản quyền từ NXB Larousse danh tiếng của Ph&aacute;p.<br />\r\n<br />\r\n<strong>Tr&iacute;ch đoạn hay:</strong></p>\r\n\r\n<p>1. Năm 1872, trong một lần lưu lại th&agrave;nh phố Le Havre, trong v&agrave;i giờ Monet đ&atilde; ph&aacute;c thảo một bức vẽ c&oacute; k&iacute;ch thước khi&ecirc;m tốn, thoạt ti&ecirc;n c&oacute; t&ecirc;n l&agrave; Biển (Marine) v&agrave; c&oacute; vẻ kh&ocirc;ng được &ocirc;ng coi trọng. Hai năm sau đ&oacute;, khi bức tranh được trưng b&agrave;y trước c&ocirc;ng ch&uacute;ng tại triển l&atilde;m [do Nadar tổ chức] n&agrave;y, dưới ti&ecirc;u đề Ấn tượng, Mặt Trời mọc (Impression, soleil levant), n&oacute; đ&atilde; g&acirc;y cười cho giới ph&ecirc; b&igrave;nh, b&agrave;i b&igrave;nh luận nổi tiếng nhất vẫn l&agrave; của Louis Leroy, nh&agrave; b&aacute;o của tờ Charivari, trong ph&oacute;ng sự ng&agrave;y 28 th&aacute;ng 4 với nhan đề &ldquo;Triển l&atilde;m của những nghệ sĩ Ấn tượng&rdquo;:</p>\r\n\r\n<p>Ồ! Đ&oacute; l&agrave; một ng&agrave;y kh&oacute; nhằn, khi t&ocirc;i mạo hiểm đến cuộc triển l&atilde;m đầu ti&ecirc;n tr&ecirc;n đại lộ Les Capucines c&ugrave;ng với Joseph Vincent, họa sĩ vẽ tranh phong cảnh, học tr&ograve; của Bertin, người được nhiều ch&iacute;nh phủ trao tặng huy chương v&agrave; hu&acirc;n chương! Một kẻ liều lĩnh đ&atilde; đến đ&oacute; m&agrave; kh&ocirc;ng ch&uacute;t ho&agrave;i nghi; &ocirc;ng ấy nghĩ sẽ được xem những bức tranh giống như ở bất cứ nơi đ&acirc;u, đẹp v&agrave; xấu, xấu nhiều hơn l&agrave; đẹp, nhưng kh&ocirc;ng hề x&acirc;m phạm đến c&aacute;c chuẩn tắc nghệ thuật, đến việc t&ocirc;n thờ c&aacute;c mẫu h&igrave;nh v&agrave; sự k&iacute;nh trọng đối với c&aacute;c bậc thầy. [] Một thảm họa chắc chắn sắp xảy ra, v&agrave; ng&agrave;i Monet l&agrave; người gi&aacute;ng cho &ocirc;ng ấy [Joseph Vincent] đ&ograve;n cuối c&ugrave;ng. &ndash; &Agrave;! Đ&acirc;y rồi, đ&acirc;y rồi! &Ocirc;ng ấy h&eacute;t l&ecirc;n trước bức tranh số 98. T&ocirc;i đ&atilde; nhận ra bức y&ecirc;u th&iacute;ch nhất của Vincent! Bức tranh n&agrave;y vẽ g&igrave; vậy? H&atilde;y thử xem trong tập s&aacute;ch nhỏ n&agrave;y &ndash; Ấn tượng, Mặt Trời mọc. Ấn tượng, hẳn l&agrave; vậy. T&ocirc;i tự nhủ, v&igrave; t&ocirc;i ấn tượng với bức tranh n&agrave;y, n&ecirc;n chắc hẳn c&oacute; điều ấn tượng trong đ&oacute;&hellip; v&agrave; c&aacute;ch l&agrave;m thật l&agrave; tự do, thật dễ d&atilde;i! Một bản vẽ ph&aacute;c thảo thậm ch&iacute; nh&igrave;n c&ograve;n ho&agrave;n thiện hơn l&agrave; bức tranh cảnh biển kia!</p>\r\n\r\n<p>Thế l&agrave; trường ph&aacute;i Ấn tượng được khai sinh! (tr.17-18)</p>\r\n\r\n<p>2. Một giai thoại nổi tiếng kể rằng v&agrave;o một buổi s&aacute;ng cuối h&egrave; đẹp trời năm 1890, họa sĩ nhờ Blanche Hosched&eacute; mau t&igrave;m cho &ocirc;ng một tấm vải mới do &aacute;nh s&aacute;ng ở đống cỏ kh&ocirc; m&agrave; &ocirc;ng đang vẽ đ&atilde; thay đổi, v&igrave; vậy đ&atilde; thay đổi cả m&agrave;u sắc. Trong những năm cuối đời, bản th&acirc;n họa sĩ vẫn giữ g&igrave;n sự diễn giải thi vị n&agrave;y. Một l&aacute; thư Monet gửi cho Gustave Geffroy ng&agrave;y 7 th&aacute;ng 10 năm 1890 đưa ra một bằng chứng c&ograve;n r&otilde; r&agrave;ng hơn: &ldquo;T&ocirc;i cố gắng rất nhiều, t&ocirc;i ki&ecirc;n tr&igrave; tạo ra một loạt c&aacute;c hiệu ứng kh&aacute;c nhau (của c&aacute;c đống cỏ kh&ocirc;), nhưng tại thời điểm đ&oacute;, Mặt Trời lặn rất nhanh đến nỗi t&ocirc;i kh&ocirc;ng thể bắt kị T&ocirc;i trở n&ecirc;n chậm chạp, điều n&agrave;y khiến t&ocirc;i thất vọng, nhưng c&agrave;ng đi t&ocirc;i c&agrave;ng thấy m&igrave;nh cần nỗ lực rất nhiều để thể hiện được c&aacute;i t&ocirc;i đang t&igrave;m kiếm: sự tức thời, đặc biệt l&agrave; kh&ocirc;ng kh&iacute; bao tr&ugrave;m, c&ugrave;ng một thứ &aacute;nh s&aacute;ng lan tỏa khắp nơi&rdquo;. (tr.10)</p>\r\n\r\n<p>3. &ldquo;T&ocirc;i nh&igrave;n thấy m&agrave;u xanh da trời, nhưng t&ocirc;i kh&ocirc;ng thể nh&igrave;n thấy m&agrave;u đỏ, kh&ocirc;ng thể nh&igrave;n thấy m&agrave;u v&agrave;ng nữa; n&oacute; l&agrave;m t&ocirc;i kh&oacute; chịu khủng khiếp v&igrave; t&ocirc;i biết rằng những m&agrave;u n&agrave;y tồn tại; bởi v&igrave; t&ocirc;i biết rằng tr&ecirc;n bảng m&agrave;u của t&ocirc;i c&oacute; m&agrave;u đỏ, v&agrave;ng, c&oacute; một m&agrave;u xanh l&aacute; c&acirc;y đặc biệt, một m&agrave;u t&iacute;m nhất định; t&ocirc;i kh&ocirc;ng c&ograve;n nh&igrave;n thấy ch&uacute;ng như khi t&ocirc;i đ&atilde; thấy ch&uacute;ng ng&agrave;y trước nữa, mặc d&ugrave; t&ocirc;i nhớ rất r&otilde; m&agrave;u sắc m&agrave; ch&uacute;ng mang lại&rdquo;. (b&aacute;c sĩ Jacques Mawas thuật lại lời của Monet, tr.29)</p>\r\n\r\n<p>4. L&agrave;m sao c&oacute; thể kh&ocirc;ng đề cập ở đ&acirc;y ảnh hưởng dễ thấy của nghệ thuật nhiếp ảnh vừa mới ra đời, &iacute;t nhất l&agrave; khi n&oacute;i đến khung h&igrave;nh? C&aacute;ch lựa chọn khung h&igrave;nh n&agrave;y khiến người ta nghĩ đến nhiếp ảnh, thứ được coi l&agrave; &ldquo;thư ph&aacute;p của &aacute;nh s&aacute;ng&rdquo; do Nic&eacute;phore Niepce ph&aacute;t minh v&agrave;i thập kỷ trước đ&oacute;. Cuối c&ugrave;ng, bức tranh n&agrave;y khẳng định sự tự do của mặt phẳng vẽ, kh&ocirc;ng thật sự c&oacute; sự ph&acirc;n cấp giữa c&aacute;c lớp cảnh, v&agrave; m&agrave;u sắc l&agrave; n&eacute;t quyến rũ duy nhất. (tr.36)</p>\r\n\r\n<p>5. Phương thức của nghệ sĩ chưa hề c&oacute; tiền lệ trong lịch sử; c&aacute;c họa sĩ cổ điển &ldquo;chế t&aacute;c&rdquo; phong cảnh của họ, những người L&atilde;ng mạn th&igrave; mơ về ch&uacute;ng. Monet l&ecirc;n &yacute; tưởng, thiết kế v&agrave; tạo ra ch&uacute;ng trong khu vườn của m&igrave;nh, để sau đ&oacute; &ocirc;ng chỉ việc vẽ. Do đ&oacute;, &ocirc;ng ho&agrave;n to&agrave;n tự do trong việc lựa chọn chủ thể để t&aacute;i hiện, m&agrave; kh&ocirc;ng lo lệ thuộc v&agrave;o thi&ecirc;n nhi&ecirc;n. (tr.121)</p>\r\n\r\n<p>6. Trong tất cả c&aacute;c đặc t&iacute;nh tiền th&acirc;n của nghệ thuật của Monet, nguy&ecirc;n tắc lặp lại dễ d&agrave;ng thắng thế. Một nguy&ecirc;n tắc, dựa tr&ecirc;n sự đa dạng của c&aacute;c h&igrave;nh khối tự nhi&ecirc;n, t&igrave;m kiếm những thứ đảm bảo sự thống nhất của n&oacute;. Do đ&oacute;, dưới n&eacute;t cọ của họa sĩ, một học thuyết ca tụng đồng thời sự tr&agrave;n ngập c&aacute;c h&igrave;nh mẫu tự nhi&ecirc;n v&agrave; sự cạn kiệt khả năng trang tr&iacute; đ&atilde; nảy sinh; b&aacute;o trước nền nghệ thuật m&ocirc;i trường lần thứ hai của thế kỷ XX, c&aacute;c t&aacute;c phẩm cuối c&ugrave;ng mang đến cho người xem cảm gi&aacute;c được đắm ch&igrave;m trong một vũ trụ hỗn loạn v&agrave; đơn nhất đầy nghịch l&yacute;. N&oacute;i t&oacute;m lại, một sự sống động. (tr.123)</p>\r\n\r\n<p><strong>C&acirc;u Quote hay:</strong></p>\r\n\r\n<p>1. &ldquo;Chủ thể chỉ l&agrave; phụ; c&aacute;i t&ocirc;i muốn t&aacute;i dựng l&agrave; những g&igrave; diễn ra giữa t&ocirc;i v&agrave; chủ thể.&rdquo; (Monet, tr.41)</p>\r\n\r\n<p>2. &ldquo;Nghệ thuật biến mất, im tiếng v&agrave; ta chỉ c&ograve;n thấy ch&iacute;nh m&igrave;nh trong sự hiện diện của một thi&ecirc;n nhi&ecirc;n sống động đ&atilde; ho&agrave;n to&agrave;n bị chinh phục v&agrave; chế ngự bởi người họa sĩ phi thường n&agrave;y.&rdquo; (tr.54)</p>\r\n\r\n<p>3. &ldquo;T&ocirc;i chỉ nh&igrave;n v&agrave;o những g&igrave; vũ trụ b&agrave;y ra trước mắt để l&agrave;m chứng cho vũ trụ bằng cọ vẽ của t&ocirc;i.&rdquo; (Monet, tr.120)</p>\r\n\r\n<p><strong>Về t&aacute;c giả:</strong></p>\r\n\r\n<p>G&Eacute;RARD DENIZEAU (25/10/1953)</p>\r\n\r\n<p>Nh&agrave; nghi&ecirc;n cứu lịch sử nghệ thuật, nh&agrave; &acirc;m nhạc v&agrave; c&acirc;y viết người Ph&aacute;p với rất nhiều t&aacute;c phẩm thuộc đủ thể loại: tiểu thuyết, thơ ca, b&aacute;o ch&iacute;, s&aacute;ch về mỹ thuật v&agrave; &acirc;m nhạc.</p>\r\n\r\n<p>&Ocirc;ng từng l&agrave;m gi&aacute;o vi&ecirc;n trung học (từ năm 1978 tại Lons-le-Saunier), giảng vi&ecirc;n tại Đại học Lorraine (từ năm 1984), giảng vi&ecirc;n &acirc;m nhạc tại Đại học Paris 4 (từ năm 1992) v&agrave; tại Trung t&acirc;m Đ&agrave;o tạo Nghệ thuật cao cấp CRR-Paris 4 (từ năm 2004).</p>\r\n\r\n<p>G&eacute;rard Denizeau đ&atilde; d&agrave;nh nhiều năm trong sự nghiệp viết l&aacute;ch để cống hiến cho sự bảo tồn những di sản qu&yacute; b&aacute;u của nước Ph&aacute;p n&oacute;i ri&ecirc;ng v&agrave; thế giới n&oacute;i chung.</p>\r\n', 1, 1, 10, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(64, 'Paul Gauguin', 'ada5247aee1d04560679c81cb65367b2.jpg', 1, 178000, 'Là một nhà du hành đích thực, Paul Gauguin ghi tên mình vào danh sách các họa sĩ Pháp vĩ đại nhất thế kỷ XIX như người mở đường cho nghệ thuật hiện đại.\r\nỞ Gauguin, ta thấy dấu vết của trường phái Tượng trưng, trường phái Ấn tượng, phong cách Điểm màu, nh', '<p>L&agrave; một nh&agrave; du h&agrave;nh đ&iacute;ch thực, Paul Gauguin ghi t&ecirc;n m&igrave;nh v&agrave;o danh s&aacute;ch c&aacute;c họa sĩ Ph&aacute;p vĩ đại nhất thế kỷ XIX như người mở đường cho nghệ thuật hiện đại.<br />\r\nỞ Gauguin, ta thấy dấu vết của trường ph&aacute;i Tượng trưng, trường ph&aacute;i Ấn tượng, phong c&aacute;ch Điểm m&agrave;u, nhưng tr&ecirc;n hết, &ocirc;ng kh&ocirc;ng ho&agrave;n to&agrave;n đi theo tr&agrave;o lưu n&agrave;o trong số đ&oacute;. Nổi tiếng với những bức ch&acirc;n dung phụ nữ Tahiti hay Breton sặc sỡ, khoa trương, Gauguin kh&aacute;m ph&aacute; gần như mọi ng&oacute;c ng&aacute;ch của nghệ thuật: vẽ, in khắc l&otilde;m, đi&ecirc;u khắc, gốm sứ&hellip; suốt h&agrave;nh tr&igrave;nh l&agrave;m mới li&ecirc;n tục để tạo ra bảng m&agrave;u độc đ&aacute;o cho bản th&acirc;n, kh&iacute;a cạnh m&agrave; &ocirc;ng vừa l&agrave; nh&agrave; ti&ecirc;n phong vừa l&agrave; một bậc thầy.</p>\r\n\r\n<p>Từ g&oacute;c nh&igrave;n mới mẻ đ&oacute;, cuốn s&aacute;ch ph&acirc;n t&iacute;ch kỹ cho ta thấy về cuộc đời, t&iacute;nh c&aacute;ch v&agrave; con người, cũng như phong c&aacute;ch, quan điểm của Gauguin.<br />\r\nĐồng thời cuốn s&aacute;ch được minh họa tuyệt đẹp với rất nhiều h&igrave;nh ảnh n&agrave;y sẽ l&agrave;m s&aacute;ng tỏ những h&agrave;nh tr&igrave;nh qua lại của Gauguin giữa nước Ph&aacute;p v&agrave; v&ugrave;ng Tahiti, mang đến những ph&acirc;n t&iacute;ch chặt chẽ m&agrave; qua đ&oacute;, người đọc sẽ c&oacute; thể hiểu tr&iacute; tưởng tượng c&ugrave;ng những giấc mơ của Gauguin đ&atilde; đưa &ocirc;ng đến đ&acirc;u trong sự nghiệp nghệ thuật.</p>\r\n\r\n<p>Những người y&ecirc;u mến danh họa Paul Gauguin v&agrave; muốn t&igrave;m hiểu về cuộc đời &ocirc;ng, c&ugrave;ng th&ocirc;ng tin về c&aacute;c kiệt t&aacute;c của &ocirc;ng sẽ kh&ocirc;ng thể bỏ qua t&aacute;c phẩm n&agrave;y.</p>\r\n\r\n<p>Cuốn s&aacute;ch nằm trong bộ s&aacute;ch về c&aacute;c t&aacute;c giả v&agrave; t&aacute;c phẩm hội họa nổi tiếng, thuộc tủ s&aacute;ch nghệ thuật của Omega+, mua bản quyền từ NXB Larousse danh tiếng của Ph&aacute;p.</p>\r\n\r\n<p><strong>Tr&iacute;ch đoạn hay:</strong></p>\r\n\r\n<p>1. Từ mong muốn truyền tải v&agrave; giải th&iacute;ch ban đầu, người họa sĩ dần l&agrave;m việc theo lối mang t&iacute;nh ch&iacute;nh trị v&agrave; chiến lược nhiều hơn. Lo lắng khi nghĩ về những g&igrave; người kh&aacute;c sẽ n&oacute;i về m&igrave;nh, &ocirc;ng th&iacute;ch tự tạo ra huyền thoại cho ri&ecirc;ng m&igrave;nh, ngay cả khi n&oacute; mang nghĩa ph&oacute;ng đại, thực hiện một ch&acirc;n dung tự họa với c&aacute;c đường n&eacute;t r&otilde; r&agrave;ng, nhưng rồi n&oacute; kh&ocirc;ng g&acirc;y được ấn tượng g&igrave;. &Ocirc;ng viết c&aacute;c văn bản tự truyện trong đ&oacute; pha trộn những kỷ niệm với c&aacute;c ghi ch&uacute; về hội họa. Trước v&agrave; sau, văn bản nổi tiếng nhất, được viết v&agrave;o năm 1902, kể về những bước đi đầu ti&ecirc;n, c&aacute;c cuộc gặp gỡ, từ Bernard cho đến Van Gogh v&agrave; C&eacute;zanne. &Ocirc;ng lu&ocirc;n tự m&ocirc; tả m&igrave;nh l&agrave; người cho ph&eacute;p những nghệ sĩ kh&aacute;c n&oacute;i về bản th&acirc;n. Chẳng hạn, nếu &ocirc;ng nhấn mạnh v&agrave;o sự ch&iacute;n chắn của m&igrave;nh vốn đối lập với sự nhẹ nh&agrave;ng, sự v&ocirc; tư thuần khiết của Van Gogh th&igrave; cũng l&agrave; v&igrave; &ocirc;ng sợ một điều: bị hiểu nhầm l&agrave; người theo g&oacute;t ai đ&oacute;, l&agrave; kẻ bắt chước, v&agrave; bị l&atilde;ng qu&ecirc;n. Hiện nay, hậu thế cho thấy một c&aacute;ch ho&agrave;n hảo rằng, &ocirc;ng đ&atilde; truyền cảm hứng rất lớn, nhưng &ocirc;ng cũng kh&ocirc;ng hề bắt chước, hay ho&agrave;n thiện để được đặt b&ecirc;n cạnh c&aacute;c c&aacute; t&iacute;nh nghệ thuật c&oacute; li&ecirc;n quan đến t&ecirc;n gọi &ldquo;chủ nghĩa&rdquo; v&agrave; c&aacute;c phong tr&agrave;o. (tr.27)</p>\r\n\r\n<p>2. &ldquo;Nếu phải định nghĩa ngắn gọn về nghệ thuật, cha sẽ gọi n&oacute; l&agrave; sự t&aacute;i tạo những g&igrave; m&agrave; gi&aacute;c quan cảm nhận được trong tự nhi&ecirc;n qua bức m&agrave;n t&acirc;m hồn.&rdquo; Paul Gauguin, Ghi ch&eacute;p cho Aline (tr.29)</p>\r\n\r\n<p><strong>Về t&aacute;c giả:</strong></p>\r\n\r\n<p>LAURE-CAROLINE SEMMER (1976)</p>\r\n\r\n<p>Giảng vi&ecirc;n m&ocirc;n lịch sử nghệ thuật tại Paris, đặc biệt tại trường Đại học Paris I &ndash; Pantheon-Sorbonne; t&aacute;c giả của một luận &aacute;n viết về c&aacute;c t&aacute;c phẩm của danh họa C&eacute;zanne.</p>\r\n\r\n<p>C&aacute;c t&aacute;c phẩm của Semmer do Larousse ấn h&agrave;nh:</p>\r\n\r\n<p>1. Lire la peinture de C&eacute;zanne [T&igrave;m hiểu tranh C&eacute;zanne] (2006)<br />\r\n2. Les oeuvres-cl&eacute;s de l&#39;impressionnisme [Từ kh&oacute;a để hiểu Chủ nghĩa Ấn tượng] (2007)<br />\r\n3. L&#39;Art abstrait [Nghệ thuật Trừu tượng] (2010)<br />\r\n4. Paul Gauguin (2017)</p>\r\n', 1, 1, 10, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(65, 'Đây Là Gauguin', 'a3d456a60b308bf391fdf08f434181d9.jpg', 1, 111000, 'Đây là Gauguin kể về cuộc đời và sự nghiệp của họa sỹ Hậu Ấn tượng vĩ đại người Pháp Gauguin, từ cậu bé có thân thế đặc biệt đến chàng trai thích phiêu lưu, mạo hiểm rồi trở thành một viên chức môi giới chứng khoán và cuối cùng lựa chọn nghệ thuật là lối ', '<p><strong>Đ&acirc;y l&agrave; Gauguin</strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; Gauguin kể về cuộc đời v&agrave; sự nghiệp của họa sỹ Hậu Ấn tượng vĩ đại người Ph&aacute;p Gauguin, từ cậu b&eacute; c&oacute; th&acirc;n thế đặc biệt đến ch&agrave;ng trai th&iacute;ch phi&ecirc;u lưu, mạo hiểm rồi trở th&agrave;nh một vi&ecirc;n chức m&ocirc;i giới chứng kho&aacute;n v&agrave; cuối c&ugrave;ng lựa chọn nghệ thuật l&agrave; lối tho&aacute;t gi&uacute;p &ocirc;ng trốn khỏi c&aacute;i cuộc sống t&ugrave; h&atilde;m. &Ocirc;ng đ&atilde; vẽ những bức tranh đẹp nhất đời m&igrave;nh tại Tahiti &ndash; nơi &ocirc;ng xem l&agrave; thi&ecirc;n đường nhiệt đới c&ograve;n giữ vẻ hoang sơ của thi&ecirc;n nhi&ecirc;n v&agrave; bản t&iacute;nh con người.</p>\r\n\r\n<p>Trong cuốn s&aacute;ch n&agrave;y, những t&aacute;c phẩm nổi tiếng của Gauguin như Nghi&ecirc;n cứu mẫu khỏa th&acirc;n, Đứa trẻ đang ngủ, Van Gogh vẽ hoa hướng dương, Ch&uacute;ng ta đến từ đ&acirc;u? Ch&uacute;ng ta l&agrave; g&igrave;? Ch&uacute;ng ta sẽ đi đ&acirc;u? đều được giới thiệu chi tiết v&agrave; cụ thể về qu&aacute; tr&igrave;nh s&aacute;ng t&aacute;c, nội dung, kỹ thuật vẽ&hellip;</p>\r\n\r\n<p>Đối với Gauguin, người họa sỹ phải l&agrave; người c&oacute; tầm nh&igrave;n, c&oacute; thể nh&igrave;n thấy b&ecirc;n dưới lớp vỏ ngo&agrave;i của sự vật, để nghiệm ra được c&aacute;i huyền b&iacute; ẩn s&acirc;u của sự sống. &ldquo;T&ocirc;i l&agrave; m&ocirc;t nghệ sỹ vĩ đại, v&agrave; t&ocirc;i biết thế. Ch&iacute;nh điều đ&oacute; khiến t&ocirc;i chịu bao nhi&ecirc;u đau khổ. Nếu kh&ocirc;ng v&igrave; theo đuổi thi&ecirc;n hướng của bản th&acirc;n, t&ocirc;i đ&atilde; xem m&igrave;nh l&agrave; kẻ ngo&agrave;i v&ograve;ng ph&aacute;p luật.&rdquo; - Paul Gauguin</p>\r\n\r\n<p>Cuốn s&aacute;ch được viết bởi chuy&ecirc;n gia George Roddam v&agrave; minh họa bởi họa sĩ Sława Harasymowicz.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; Gauguin nằm trong bộ Đ&acirc;y l&agrave; (This is) của nh&agrave; xuất bản Laurence King (Anh quốc). Trong đợt ph&aacute;t h&agrave;nh n&agrave;y, Đ&ocirc;ng A gửi đến bạn đọc 8 cuốn s&aacute;ch giới thiệu về cuộc đời, sự nghiệp v&agrave; một số t&aacute;c phẩm đặc sắc của 8 họa sĩ nổi tiếng h&agrave;ng đầu của mỹ thuật phương T&acirc;y:</p>\r\n\r\n<p>1. Đ&acirc;y l&agrave; Dal&iacute;</p>\r\n\r\n<p>2. Đ&acirc;y l&agrave; Gauguin</p>\r\n\r\n<p>3. Đ&acirc;y l&agrave; Leonardo da Vinci</p>\r\n\r\n<p>4. Đ&acirc;y l&agrave; Matisse</p>\r\n\r\n<p>5. Đ&acirc;y l&agrave; Monet</p>\r\n\r\n<p>6. Đ&acirc;y l&agrave; Rembrandt</p>\r\n\r\n<p>7. Đ&acirc;y l&agrave; Van Gogh</p>\r\n\r\n<p>8. Đ&acirc;y l&agrave; Warhol</p>\r\n\r\n<p>Cả 8 cuốn được in m&agrave;u to&agrave;n bộ tr&ecirc;n giấy chất lượng cao, m&agrave;u sắc v&agrave; h&igrave;nh ảnh sống động, hy vọng sẽ đem đến cho bạn đọc g&oacute;c nh&igrave;n th&uacute; vị về những con người đ&atilde; g&oacute;p phần l&agrave;m n&ecirc;n diện mạo của hội họa phương T&acirc;y v&agrave; thế giới.</p>\r\n\r\n<p><strong>Th&ocirc;ng tin t&aacute;c giả:</strong></p>\r\n\r\n<p>George Roddam dạy lịch sử nghệ thuật ở bậc đại học tại Vương quốc Anh v&agrave; Hoa Kỳ. Nghi&ecirc;n cứu của &ocirc;ng chủ yếu tập trung v&agrave;o c&aacute;c phong c&aacute;ch nghệ thuật hiện đại ch&acirc;u &Acirc;u, v&agrave; &ocirc;ng đ&atilde; xuất bản nhiều c&ocirc;ng tr&igrave;nh về chủ đề n&agrave;y. &Ocirc;ng sống ở v&ugrave;ng đ&ocirc;ng nam nước Anh với vợ v&agrave; hai con trai.<br />\r\nSława Harasymowicz l&agrave; họa sỹ người Ba Lan l&agrave;m việc ở London. C&aacute;c dự &aacute;n của c&ocirc; gồm một triển l&atilde;m solo tổ chức ở Bảo t&agrave;ng Freud, London sau khi c&ocirc; xuất bản cuốn tiểu thuyết bằng h&igrave;nh Người s&oacute;i của Sigmund Freud (2012), v&agrave; một triển l&atilde;m solo ở Bảo t&agrave;ng D&acirc;n tộc học Krakow (2014). C&ocirc; được trao c&aacute;c giải Arts Foundation Fellowship (2008) v&agrave; Victoria and Albert Museum Illustration Award (2009).</p>\r\n', 1, 1, 3, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(66, 'Đây Là Monet', 'ea5661114c742f03c9c339412eacb315.jpg', 1, 180000, 'Đây là Monet\r\n\r\nOscar-Claude Monet (1840 – 1926) là tên tuổi hàng đầu của trường phái hội họa Ấn tượng Pháp. Cả cuộc đời Monet trung thành với trường phái Ấn tượng, ông cũng là người tiên phong, tạo ảnh hưởng và truyền cảm hứng lâu dài lên nhiều thế hệ họ', '<p><strong>Đ&acirc;y l&agrave; Monet</strong></p>\r\n\r\n<p>Oscar-Claude Monet (1840 &ndash; 1926) l&agrave; t&ecirc;n tuổi h&agrave;ng đầu của trường ph&aacute;i hội họa Ấn tượng Ph&aacute;p. Cả cuộc đời Monet trung th&agrave;nh với trường ph&aacute;i Ấn tượng, &ocirc;ng cũng l&agrave; người ti&ecirc;n phong, tạo ảnh hưởng v&agrave; truyền cảm hứng l&acirc;u d&agrave;i l&ecirc;n nhiều thế hệ họa sĩ. Trong suốt 86 năm tại thế, người họa sĩ ấy đ&atilde; lao động kh&ocirc;ng ngơi nghỉ v&agrave; để lại cho đời hơn 2.000 t&aacute;c phẩm.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; Monet l&agrave; cuốn s&aacute;ch kể về cuộc đời của Monet từ khi c&ograve;n l&agrave; một cậu thiếu ni&ecirc;n sớm bộc lộ t&agrave;i năng th&ocirc;ng qua c&aacute;c bức h&iacute; họa vui nhộn treo tr&ecirc;n cửa sổ tiệm tạp h&oacute;a của gia đ&igrave;nh (v&agrave; đ&atilde; đem lại cho &ocirc;ng số tiền kh&aacute; đ&aacute;ng kể ở tuổi thiếu ni&ecirc;n). May mắn được học h&agrave;nh b&agrave;i bản về mỹ thuật, nhưng cũng như rất nhiều họa sĩ kh&aacute;c, Monet đ&atilde; trải qua những khoảng thời gian khốn kh&oacute;, ngh&egrave;o t&uacute;ng, với một gia đ&igrave;nh 8 đứa con nhỏ tr&ecirc;n vai (trong đ&oacute; 6 người thực chất l&agrave; con của một người bạn - Ernest Hosched&eacute;), 2 cuộc h&ocirc;n nh&acirc;n, v&agrave; những lần gửi tranh tham gia Triển l&atilde;m Paris bị hội đồng gi&aacute;m tuyển từ chối. Với khả năng lao động nghệ thuật miệt m&agrave;i, ki&ecirc;n định, v&agrave; một ch&uacute;t nhạy b&eacute;n với thị trường nghệ thuật, dần dần Monet đ&atilde; x&acirc;y dựng được t&ecirc;n tuổi v&agrave; vị thế ri&ecirc;ng m&agrave; hiếm họa sĩ n&agrave;o c&oacute; được.</p>\r\n\r\n<p>Ở cuốn s&aacute;ch n&agrave;y, b&ecirc;n cạnh c&acirc;u chuyện cuộc đời, bạn cũng sẽ được ngắm nh&igrave;n c&aacute;c t&aacute;c phẩm của Monet, từ những bức h&iacute; họa thuở thiếu thời, đến bức tranh b&igrave;nh minh tr&ecirc;n cảng Le Havre với t&ecirc;n gọi Ấn tượng, mặt trời mọc - c&aacute;i t&ecirc;n đ&atilde; định danh cả một phong c&aacute;ch, một trường ph&aacute;i hội họa như ta đ&atilde; biết. Bạn cũng sẽ được thấy những t&aacute;c phẩm cuối đời vị danh họa, những t&aacute;c phẩm m&agrave; sau đ&oacute; vẫn để lại ảnh hưởng s&acirc;u sắc l&ecirc;n rất nhiều họa sĩ, dẫu khi cầm cọ vẽ những bức tranh ấy, thị lực của Monet đ&atilde; suy giảm mạnh.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; Monet nằm trong bộ Đ&acirc;y l&agrave; (This is) của nh&agrave; xuất bản Laurence King (Anh quốc). Trong đợt ph&aacute;t h&agrave;nh n&agrave;y, Đ&ocirc;ng A gửi đến bạn đọc 8 cuốn s&aacute;ch giới thiệu về cuộc đời, sự nghiệp v&agrave; một số t&aacute;c phẩm đặc sắc của 8 họa sĩ nổi tiếng h&agrave;ng đầu của mỹ thuật phương T&acirc;y:</p>\r\n\r\n<p>1. Đ&acirc;y l&agrave; Dal&iacute;</p>\r\n\r\n<p>2. Đ&acirc;y l&agrave; Gauguin</p>\r\n\r\n<p>3. Đ&acirc;y l&agrave; Leonardo da Vinci</p>\r\n\r\n<p>4. Đ&acirc;y l&agrave; Matisse</p>\r\n\r\n<p>5. Đ&acirc;y l&agrave; Monet</p>\r\n\r\n<p>6. Đ&acirc;y l&agrave; Rembrandt</p>\r\n\r\n<p>7. Đ&acirc;y l&agrave; Van Gogh</p>\r\n\r\n<p>8. Đ&acirc;y l&agrave; Warhol</p>\r\n\r\n<p>Cả 8 cuốn được in m&agrave;u to&agrave;n bộ tr&ecirc;n giấy chất lượng cao, m&agrave;u sắc v&agrave; h&igrave;nh ảnh sống động, hy vọng sẽ đem đến cho bạn đọc g&oacute;c nh&igrave;n th&uacute; vị về những con người đ&atilde; g&oacute;p phần l&agrave;m n&ecirc;n diện mạo của hội họa phương T&acirc;y v&agrave; thế giới.</p>\r\n\r\n<p><strong>Th&ocirc;ng tin t&aacute;c giả:</strong></p>\r\n\r\n<p>Sara Pappworth l&agrave; một sử gia nghệ thuật, nh&agrave; gi&aacute;o v&agrave; nhiếp ảnh gia, đ&atilde; tham gia giảng dạy tại c&aacute;c bảo t&agrave;ng v&agrave; ph&ograve;ng triển l&atilde;m từ hơn 20 năm nay. B&agrave; hiện l&agrave;m việc cho Tate Britain, Tate Modern, Bảo t&agrave;ng Victoria &amp; Albert, Bảo t&agrave;ng Sưu tập Wallace v&agrave; Bảo t&agrave;ng Quốc gia London.</p>\r\n\r\n<p>Aude Van Ryn theo học tại Central St Martins v&agrave; Trường Nghệ thuật Ho&agrave;ng gia London. B&agrave; từng vẽ b&iacute;ch chương s&acirc;n khấu, bao b&igrave; cho Le Pain Quotidien v&agrave; h&igrave;nh ảnh quảng c&aacute;o cho c&aacute;c kh&aacute;ch h&agrave;ng như Quỹ Tim mạch Anh quốc. Tranh minh họa của b&agrave; xuất hiện trong một số triển l&atilde;m v&agrave; show diễn của những ph&ograve;ng trưng b&agrave;y nghệ thuật ở London v&agrave; Tokyo.</p>\r\n', 1, 1, 10, 1, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00');

